const now = new Date();
now.setHours(0, 0, 0, 0); // Normalize to start of day for accurate comparison

const remindBeforeDays = 3; // Remind 3 days before due date
const remindAfterDays = 7; // Remind up to 7 days after due date

const itemsToRemind = [];

for (const item of items) {
  const invoice = item.json;

  // --- ASSUMPTIONS: Adjust these field names to match your Google Sheet columns ---
  const invoiceId = invoice.InvoiceID;
  const clientName = invoice.ClientName || 'Pelanggan Yth.'; // Default name if not provided
  const clientEmail = invoice.ClientEmail;
  const amount = invoice.Amount;
  const dueDateStr = invoice.DueDate; // e.g., 'YYYY-MM-DD' or 'MM/DD/YYYY'
  const status = invoice.Status; // e.g., 'Paid', 'Pending', 'Overdue'

  // Skip if already paid or missing critical info
  if (!invoiceId || !clientEmail || !amount || !dueDateStr || (status && status.toLowerCase() === 'paid')) {
    continue;
  }

  const dueDate = new Date(dueDateStr);
  if (isNaN(dueDate.getTime())) { // Check for invalid date
    console.warn(`Invalid DueDate for InvoiceID ${invoiceId}: ${dueDateStr}`);
    continue;
  }
  dueDate.setHours(0, 0, 0, 0); // Normalize to start of day

  const timeDiff = dueDate.getTime() - now.getTime();
  const daysDiff = Math.ceil(timeDiff / (1000 * 60 * 60 * 24)); // Days until due date (positive) or since due date (negative)

  let reminderType = null;
  let subjectPrefix = '';
  let bodyText = '';

  if (daysDiff >= 0 && daysDiff <= remindBeforeDays) {
    reminderType = 'due_soon'; // Invoice due in X days
    subjectPrefix = `Pengingat: Faktur #${invoiceId} Akan Jatuh Tempo!`;
    bodyText = `Faktur Anda #${invoiceId} sebesar Rp${amount} akan jatuh tempo pada ${dueDateStr}. Mohon segera lakukan pembayaran.`;
  } else if (daysDiff < 0 && daysDiff >= -remindAfterDays) {
    reminderType = 'overdue'; // Invoice overdue by X days
    subjectPrefix = `Segera! Faktur #${invoiceId} Sudah Jatuh Tempo!`;
    bodyText = `Faktur Anda #${invoiceId} sebesar Rp${amount} telah melewati jatuh tempo pada ${dueDateStr}. Mohon segera lakukan pembayaran Anda.`;
  }

  if (reminderType) {
    itemsToRemind.push({
      json: {
        clientName: clientName,
        clientEmail: clientEmail,
        invoiceId: invoiceId,
        amount: amount,
        dueDate: dueDateStr,
        reminderType: reminderType,
        daysDiff: Math.abs(daysDiff),
        subject: subjectPrefix,
        body: bodyText
      }
    });
  }
}

return itemsToRemind;
