const data = items[0].json.data;

let message = 'üìä *Daily P&L Report*\n\n';

const formatCurrency = (value) =>
  `‚Çπ${value.toLocaleString('en-IN', { minimumFractionDigits: 2 })}`;

const formatPercent = (value) => `${(value * 100).toFixed(2)}%`;

for (const row of data) {
  if (row.Platform === 'Total') continue; // Skip Total for now

  message += `*üîπ ${row.Platform}*\n`;
  message += `- Invested: ${formatCurrency(row["Invested Amount"])}\n`;
  message += `- P&L: ${formatCurrency(row["PNL"])} (${formatPercent(row["PNL %"])})\n`;
  message += `- Change: ${formatCurrency(row["PNL Change"])} (*${formatPercent(row["PNL Change %"]) }*)\n`;
  message += `- Current Value: ${formatCurrency(row["Invested Amount"] + row["PNL"])}\n\n`;
}

// Add Total Portfolio section
const total = data.find((d) => d.Platform === 'Total');
if (total) {
  message += `*üìà Total Portfolio*\n`;
  message += `- Total Invested: ${formatCurrency(total["Invested Amount"])}\n`;
  message += `- Total P&L: ${formatCurrency(total["PNL"])} (${formatPercent(total["PNL %"])})\n`;
  message += `- Today's Change: ${formatCurrency(total["PNL Change"])} (*${formatPercent(total["PNL Change %"]) }*)\n\n`;
  message += `üí∞ *Overall Value*: ${formatCurrency(total["Invested Amount"] + total["PNL"])}\n`;
  message += `üìà *Overall Return*: *${formatPercent(total["PNL %"])}*\n`;
  message += `üí∏ *Overall P&L*: ${formatCurrency(total["PNL"])}\n`;
}

return [{
  json: {
    chat_id:$('Edit Fields').first().json.myId ,
    text: message,
    parse_mode: "Markdown"
  }
}];

const row = items[0].json;

const formatCurrency = (value) =>
  `‚Çπ${value.toLocaleString('en-IN', { minimumFractionDigits: 2 }) }`;

const formatPercent = (value) =>
  `${(value * 100).toFixed(2)}%`;

const invested = row["Invested Amount"];
const pnl = row["PNL"];
const change = row["PNL Change"];
const pnlPercent = row["PNL %"];
const changePercent = row["PNL Change %"];
const currentValue = invested + pnl;
const platform = row["Platform"].charAt(0).toUpperCase() + row["Platform"].slice(1).toLowerCase();

const message = 
`üìä *Daily P&L Report*\n\n` +
`*üîπ ${platform}*\n` +
`- Invested: ${formatCurrency(invested)}\n` +
`- P&L: ${formatCurrency(pnl)} (${formatPercent(pnlPercent)})\n` +
`- Change: ${formatCurrency(change)} (*${formatPercent(changePercent)}*)\n` +
`- Current Value: ${formatCurrency(currentValue)}`;

return [{
  json: {
    chat_id: $('Edit Fields').first().json.myId,
    text: message,
    parse_mode: "Markdown"
  }
}];
