parameters.functionCode:
// Extract fields from email
const html = item.json.text || '';
const match = (re) => { const m = html.match(re); return m ? m[1].trim() : '' };
item.json.clientName      = match(/Meeting assets for [\s\S]*?\(([^)]+)\)\s+with/);
item.json.sessionType     = match(/\b(\d+\s*hora(?:s)?|exploratory call)\b/i);
const dt = html.match(/GMT(\d{4})(\d{2})(\d{2})-(\d{2})(\d{2})(\d{2})/);
if (dt) {
  item.json.dateTimeUTC = `${dt[1]}-${dt[2]}-${dt[3]}T${dt[4]}:${dt[5]}:${dt[6]}Z`;
}
item.json.duration        = match(/Duration:\s*([0-9]{1,2}:[0-9]{2}:[0-9]{2})/i);
item.json.recordingLink   = match(/(https:\/\/us02web\.zoom\.us\/recording\/detail\?[^)\s]+)/);
item.json.quickSummary    = match(/RESUMEN R√ÅPIDO[\s\S]*?\n([\s\S]*?)\nPR√ìXIMOS PASOS/);
item.json.nextSteps       = match(/PR√ìXIMOS PASOS[\s\S]*?\n([\s\S]*?)\nRESUMEN/);
item.json.detailedSummary = match(/RESUMEN(?!\s*R√ÅPIDO)\s*([\s\S]*?)(?=\s*(?:PR√ìXIMOS PASOS|AI can make mistakes))/i);
return item;

