const inputData = items[0].json.body;

// --- IMPORTANT: ADJUST THESE FIELD NAMES TO MATCH YOUR INCOMING DATA ---
// Common assumption: Incoming data contains 'candidateName', 'position', 'newStatus', and optionally 'oldStatus' and 'notes'.
// Example:
// If your system sends: { "applicant_name": "John Doe", "job_role": "Software Engineer", "current_stage": "Interview Scheduled" }
// Adjust the variables below:
// const candidateName = inputData.applicant_name;
// const position = inputData.job_role;
// const newStatus = inputData.current_stage;

const candidateName = inputData.candidateName || inputData.applicant_name || 'Unknown Candidate';
const position = inputData.position || inputData.job_role || 'Unknown Position';
const newStatus = inputData.newStatus || inputData.current_status || 'No New Status';
const oldStatus = inputData.oldStatus || inputData.previous_status || null;
const notes = inputData.notes || inputData.comment || ''; // Optional: for additional notes

if (!candidateName || !newStatus) {
  throw new Error('Candidate data (name or new status) is incomplete. Please check your data sender configuration.');
}

// Prepare message for Slack/Email
let statusMessage;
if (oldStatus && newStatus !== oldStatus) {
    statusMessage = `Candidate Status Updated: *${candidateName}* for position *${position}* changed from *${oldStatus}* to *${newStatus}*.`;
} else {
    statusMessage = `New Candidate Status: *${candidateName}* for position *${position}* is now *${newStatus}*.`;
}

if (notes) {
    statusMessage += `\nNotes: ${notes}`; // Add notes if available
}

return [{
    json: {
        candidateName: candidateName,
        position: position,
        newStatus: newStatus,
        oldStatus: oldStatus,
        slackMessage: statusMessage, // Message formatted for Slack (supports simple Markdown)
        emailSubject: `Candidate Status Update: ${candidateName} (${position})`,
        emailBody: statusMessage.replace(/\*/g, '**') // Convert Slack Markdown to bold for email
    }
}];
