parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "slug": {
      "type": "string",
      "description": "Proposed image filename or identifier (e.g., 'ev-sales-2024')"
    },
    "width": {
      "type": "string",
      "description": "Pixel width of the image canvas (e.g., '500')"
    },
    "height": {
      "type": "string",
      "description": "Pixel height of the image canvas (e.g., '300')"
    },
    "devicePixelRatio": {
      "type": "number",
      "description": "Device pixel ratio (e.g., 2)"
    },
    "format": {
      "type": "string",
      "enum": ["png", "jpeg", "webp", "svg", "pdf"],
      "description": "Output format of the image"
    },
    "backgroundColor": {
      "type": "string",
      "description": "Canvas background color in hex or named CSS color, make it white #FFFFFF by default"
    },
    "version": {
      "type": "string",
      "description": "Chart.js version (e.g., '3.9.1')"
    },
    "chart": {
      "type": "object",
      "properties": {
        "type": {
          "type": "string",
          "enum": ["line", "bar", "doughnut", "pie", "polarArea"]
        },
        "data": {
          "type": "object",
          "properties": {
            "labels": {
              "type": "array",
              "items": { "type": "string" },
              "minItems": 1
            },
            "datasets": {
              "type": "array",
              "items": {
                "type": "object",
                "properties": {
                  "label": { "type": "string" },
                  "data": {
                    "type": "array",
                    "items": { "type": "number" },
                    "minItems": 1
                  },
                  "backgroundColor": {
                    "type": ["string", "array"],
                    "items": { "type": "string" }
                  },
                  "borderColor": {
                    "type": ["string", "array"],
                    "items": { "type": "string" }
                  },
                  "fill": { "type": ["boolean", "string"] },
                  "borderWidth": { "type": "number" }
                },
                "required": ["label", "data"]
              },
              "minItems": 1
            }
          },
          "required": ["labels", "datasets"]
        },
        "options": {
          "type": "object",
          "description": "Chart.js configuration options including title, plugins, scales, etc."
        }
      },
      "required": ["type", "data"]
    }
  },
  "required": ["slug", "chart"],
  "default": {
    "format": "png",
    "backgroundColor": "white"
  }
}

