// Extract required fields from each product item
const products = [];

// Loop through all input items
for (const item of $input.all()) {
  const productData = {
    title: item.json.title || 'N/A',
    price: item.json.price?.value || 0,
    stars: item.json.stars || 0,
    totalReviews: calculateTotalReviews(item.json.starsBreakdown),
    productUrl: item.json.url || 'N/A',
    productDescription: item.json.description || 'N/A' // Using title as description since description field isn't available
  };

  products.push(productData);
}

// Function to calculate total reviews from stars breakdown
function calculateTotalReviews(starsBreakdown) {
  if (!starsBreakdown) return 0;

  // Sum up all review percentages to estimate total reviews
  // Note: This gives relative distribution, not absolute count
  const total = (starsBreakdown['5star'] || 0) + 
                (starsBreakdown['4star'] || 0) + 
                (starsBreakdown['3star'] || 0) + 
                (starsBreakdown['2star'] || 0) + 
                (starsBreakdown['1star'] || 0);

  return Math.round(total * 100); // Convert to percentage format
}

// Return the processed data
return products.map(product => ({ json: product }));
