// cleanAnswer – run once per item
let txt = $('AI Agent').first().json.output || '';

// 1. Remove bold / italic / strike markers
txt = txt.replace(/[*_~]+/g, '');

// 2. Convert [Texto](https://url) → Texto https://url
txt = txt.replace(/\[([^\]]+)\]\((https?:\/\/[^\s)]+)\)/g, '$1 $2');

// 3. Collapse 3+ blank lines
txt = txt.replace(/\n{3,}/g, '\n\n').trim();

// 4. Remove the unwanted source-reference preamble
txt = txt.replace(/^.*?based on the document you provided[,:]?\s*/i, '');

return [{ json: { answer: txt } }];

// within24h?  – run once per item
// Meta (WhatsApp) timestamp arrives as seconds since epoch
const lastTs = Number($('when message received').first().json.messages[0].timestamp) * 1000;   // → ms
const withinWindow = Date.now() - lastTs < 24 * 60 * 60 * 1000;

return [{ json: { withinWindow, answer: $json.answer, userId: $json.userId } }];

const googleDocs = $input.all().map((item) => item.json);
const whatsappMessages = $("when message received")
  .all()
  .map((item) => item.json);

const date = new Date();
const formattedDate = `${date.getMonth() + 1} ${date.getDate()}, ${date.getFullYear()}`;

const docText = googleDocs[0].content.split("\n").join(" ");

const body = whatsappMessages[0].messages[0].text.body;

const finalPrompt = `Today's date is: ${formattedDate}\n\n${docText}\n\nUser's question:\n${body}`;

return { finalPrompt };
