import json
from datetime import datetime
import math

# Get the weather data from the previous node
weather_data = items[0]['json']

# Extract relevant information
city = weather_data['name']
country = weather_data['sys']['country']
temperature = weather_data['main']['temp']
feels_like = weather_data['main']['feels_like']
humidity = weather_data['main']['humidity']
pressure = weather_data['main']['pressure']
description = weather_data['weather'][0]['description']
wind_speed = weather_data['wind']['speed']
visibility = weather_data.get('visibility', 0) / 1000  # Convert to km

# Calculate temperature in Fahrenheit
temp_fahrenheit = (temperature * 9/5) + 32
feels_like_fahrenheit = (feels_like * 9/5) + 32

# Calculate wind speed in different units
wind_speed_kmh = wind_speed * 3.6
wind_speed_mph = wind_speed * 2.237

# Determine comfort level based on temperature and humidity
def get_comfort_level(temp, humidity):
    if temp < 0:
        return "Freezing"
    elif temp < 10:
        return "Cold"
    elif temp > 35:
        return "Very Hot"
    elif temp > 28:
        return "Hot"
    elif humidity > 80:
        return "Humid"
    elif 18 <= temp <= 24 and 40 <= humidity <= 60:
        return "Ideal"
    elif 15 <= temp <= 27 and humidity <= 70:
        return "Comfortable"
    else:
        return "Moderate"

# Get clothing suggestion
def get_clothing_suggestion(temp, wind, desc):
    if "rain" in desc.lower() or "drizzle" in desc.lower():
        clothing = "Waterproof jacket and umbrella"
    elif "snow" in desc.lower():
        clothing = "Heavy winter coat, boots, gloves, and hat"
    elif temp < 0:
        clothing = "Heavy winter coat, thermal layers, gloves, and hat"
    elif temp < 10:
        clothing = "Warm jacket or heavy sweater"
    elif temp < 18:
        clothing = "Light jacket or cardigan"
    elif temp < 25:
        clothing = "Long sleeves or light sweater"
    elif temp < 30:
        clothing = "T-shirt and light pants/shorts"
    else:
        clothing = "Light, breathable clothing and sun protection"

    if wind > 5:
        clothing += " (consider wind protection)"

    return clothing

# Get activity recommendation
def get_activity_recommendation(temp, desc, wind):
    desc_lower = desc.lower()

    if "thunderstorm" in desc_lower or "heavy rain" in desc_lower:
        return "Stay indoors, perfect for indoor activities"
    elif "rain" in desc_lower or "drizzle" in desc_lower:
        return "Indoor activities recommended, or covered outdoor areas"
    elif "snow" in desc_lower:
        return "Winter sports, snowman building, or cozy indoor activities"
    elif temp < -5:
        return "Limit outdoor exposure, indoor activities recommended"
    elif temp < 5:
        return "Short outdoor activities, winter sports, or indoor activities"
    elif 15 <= temp <= 25 and wind < 7:
        return "Perfect for any outdoor activities - hiking, sports, picnics"
    elif temp > 30:
        return "Early morning or evening outdoor activities, stay hydrated"
    elif wind > 10:
        return "Be cautious with outdoor activities, secure loose items"
    else:
        return "Good for moderate outdoor activities"

# Determine weather emoji
def get_weather_emoji(desc):
    desc_lower = desc.lower()
    if "clear" in desc_lower:
        return "‚òÄÔ∏è"
    elif "cloud" in desc_lower:
        return "‚òÅÔ∏è" if "few" not in desc_lower else "‚õÖ"
    elif "rain" in desc_lower:
        return "üåßÔ∏è"
    elif "thunderstorm" in desc_lower:
        return "‚õàÔ∏è"
    elif "snow" in desc_lower:
        return "‚ùÑÔ∏è"
    elif "mist" in desc_lower or "fog" in desc_lower:
        return "üå´Ô∏è"
    else:
        return "üå§Ô∏è"

comfort_level = get_comfort_level(temperature, humidity)
weather_emoji = get_weather_emoji(description)

# Create a comprehensive weather report
weather_report = {
    "location": f"{city}, {country}",
    "timestamp": datetime.now().strftime("%Y-%m-%d %H:%M:%S"),
    "weather_emoji": weather_emoji,
    "current_conditions": {
        "temperature_celsius": round(temperature, 1),
        "temperature_fahrenheit": round(temp_fahrenheit, 1),
        "feels_like_celsius": round(feels_like, 1),
        "feels_like_fahrenheit": round(feels_like_fahrenheit, 1),
        "description": description.title(),
        "humidity": humidity,
        "pressure": pressure,
        "visibility_km": round(visibility, 1),
        "wind_speed_ms": round(wind_speed, 1),
        "wind_speed_kmh": round(wind_speed_kmh, 1),
        "wind_speed_mph": round(wind_speed_mph, 1)
    },
    "analysis": {
        "comfort_level": comfort_level,
        "is_good_weather": temperature >= 15 and temperature <= 25 and humidity <= 70 and "rain" not in description.lower(),
        "clothing_suggestion": get_clothing_suggestion(temperature, wind_speed, description),
        "activity_recommendation": get_activity_recommendation(temperature, description, wind_speed),
        "weather_quality_score": min(100, max(0, 
            100 - abs(20 - temperature) * 3 - max(0, humidity - 60) - max(0, wind_speed - 5) * 5
        ))
    },
    "raw_data": weather_data
}

# Return the processed data
return [{"json": weather_report}]
