// Process extracted product data and analyze price changes
const inputData = $input.all();
const processedProducts = [];

// Configuration for tracking specific products/brands
const trackingConfig = {
  targetBrands: ['Sony', 'Apple', 'Bose', 'Sennheiser', 'Audio-Technica', 'Beats'],
  priceThresholds: {
    significant_drop: 0.15, // 15% price drop
    significant_increase: 0.10, // 10% price increase
    discount_threshold: 0.20 // 20%+ discount worth noting
  },
  competitors: {
    'Amazon': { weight: 0.4, priority: 'High' },
    'Best Buy': { weight: 0.3, priority: 'Medium' },
    'Target': { weight: 0.3, priority: 'Medium' }
  }
};

// Function to calculate price insights
function analyzePricing(product, source) {
  const insights = {
    is_on_sale: false,
    discount_level: 'none',
    price_competitiveness: 'unknown',
    value_score: 0
  };

  // Check if product is on sale
  if (product.original_price && product.current_price < product.original_price) {
    insights.is_on_sale = true;
    const discountPercent = product.discount_percentage || 
      ((product.original_price - product.current_price) / product.original_price * 100);

    if (discountPercent >= 30) insights.discount_level = 'high';
    else if (discountPercent >= 15) insights.discount_level = 'moderate';
    else insights.discount_level = 'low';
  }

  // Calculate value score (price vs rating)
  if (product.rating && product.current_price) {
    insights.value_score = (product.rating * 20) / product.current_price;
  }

  return insights;
}

// Function to detect significant changes (would compare with historical data)
function detectPriceChanges(product, source) {
  // In a real implementation, this would compare with previous prices from database
  // For now, we'll simulate some logic based on discount levels
  const changes = {
    price_direction: 'stable',
    change_significance: 'none',
    alert_worthy: false,
    change_percentage: 0
  };

  // Simulate price change detection based on discount percentage
  if (product.discount_percentage) {
    changes.price_direction = 'down';
    changes.change_percentage = -product.discount_percentage;

    if (product.discount_percentage >= trackingConfig.priceThresholds.significant_drop * 100) {
      changes.change_significance = 'high';
      changes.alert_worthy = true;
    } else if (product.discount_percentage >= 10) {
      changes.change_significance = 'moderate';
      changes.alert_worthy = true;
    }
  }

  return changes;
}

// Function to generate competitive intelligence
function generateCompetitiveIntel(product, source) {
  return {
    market_position: product.current_price < 100 ? 'budget' : 
                    product.current_price < 300 ? 'mid-range' : 'premium',
    brand_strength: trackingConfig.targetBrands.includes(product.brand) ? 'strong' : 'moderate',
    source_reliability: trackingConfig.competitors[source]?.weight || 0.2,
    customer_satisfaction: product.rating >= 4.0 ? 'high' : 
                          product.rating >= 3.0 ? 'moderate' : 'low'
  };
}

// Process each input (multiple HTTP requests from different sources)
inputData.forEach(input => {
  const sourceUrl = input.json.website_url || 'unknown';
  const sourceName = sourceUrl.includes('amazon') ? 'Amazon' :
                    sourceUrl.includes('bestbuy') ? 'Best Buy' :
                    sourceUrl.includes('target') ? 'Target' : 'Unknown';

  if (input.json.result && input.json.result.products) {
    input.json.result.products.forEach(product => {
      // Only process products from target brands or above certain rating
      if (trackingConfig.targetBrands.includes(product.brand) || 
          (product.rating && product.rating >= 4.0)) {

        const pricingInsights = analyzePricing(product, sourceName);
        const priceChanges = detectPriceChanges(product, sourceName);
        const competitiveIntel = generateCompetitiveIntel(product, sourceName);

        processedProducts.push({
          json: {
            // Product Identification
            product_id: `${sourceName.toLowerCase()}_${product.model_number || Date.now()}_${Math.random().toString(36).substr(2, 5)}`,
            product_name: product.product_name,
            brand: product.brand,
            model_number: product.model_number,
            category: product.category,

            // Pricing Data
            current_price: product.current_price,
            original_price: product.original_price,
            discount_percentage: product.discount_percentage,
            currency: product.currency || 'USD',

            // Source Information
            source: sourceName,
            source_url: sourceUrl,
            product_url: product.product_url,
            source_priority: trackingConfig.competitors[sourceName]?.priority || 'Low',

            // Product Quality Metrics
            rating: product.rating,
            review_count: product.review_count,
            availability: product.availability,
            shipping_info: product.shipping_info,

            // Pricing Insights
            is_on_sale: pricingInsights.is_on_sale,
            discount_level: pricingInsights.discount_level,
            value_score: Math.round(pricingInsights.value_score * 100) / 100,

            // Price Change Analysis
            price_direction: priceChanges.price_direction,
            change_significance: priceChanges.change_significance,
            alert_worthy: priceChanges.alert_worthy,
            change_percentage: priceChanges.change_percentage,

            // Competitive Intelligence
            market_position: competitiveIntel.market_position,
            brand_strength: competitiveIntel.brand_strength,
            customer_satisfaction: competitiveIntel.customer_satisfaction,

            // Metadata
            scraped_at: new Date().toISOString(),
            tracking_priority: priceChanges.alert_worthy ? 'High' : 
                             pricingInsights.is_on_sale ? 'Medium' : 'Low',

            // Features for analysis
            key_features: product.key_features || [],
            image_url: product.image_url
          }
        });
      }
    });
  }
});

return processedProducts;

