// Advanced Customer Support Analytics & Intelligence Processing
const inputData = $input.all();
const processedTickets = [];
const analyticsData = [];

// Configuration for support analytics and escalation rules
const supportConfig = {
  slaThresholds: {
    'Critical': { first_response_minutes: 15, resolution_hours: 4 },
    'High': { first_response_minutes: 30, resolution_hours: 8 },
    'Medium': { first_response_minutes: 120, resolution_hours: 24 },
    'Low': { first_response_minutes: 480, resolution_hours: 72 }
  },
  customerTiers: {
    'Enterprise': { priority_multiplier: 1.5, escalation_threshold: 0.5 },
    'Premium': { priority_multiplier: 1.2, escalation_threshold: 0.7 },
    'Standard': { priority_multiplier: 1.0, escalation_threshold: 1.0 },
    'Basic': { priority_multiplier: 0.8, escalation_threshold: 1.2 }
  },
  categoryPatterns: {
    'Technical': {
      keywords: ['error', 'bug', 'not working', 'crash', 'performance', 'integration'],
      avg_resolution_hours: 12,
      escalation_rate: 0.15
    },
    'Billing': {
      keywords: ['payment', 'invoice', 'charge', 'refund', 'subscription', 'billing'],
      avg_resolution_hours: 6,
      escalation_rate: 0.08
    },
    'Account': {
      keywords: ['login', 'password', 'access', 'permissions', 'account', 'profile'],
      avg_resolution_hours: 4,
      escalation_rate: 0.05
    },
    'Feature Request': {
      keywords: ['feature', 'enhancement', 'improvement', 'suggestion', 'request'],
      avg_resolution_hours: 48,
      escalation_rate: 0.03
    },
    'General Inquiry': {
      keywords: ['question', 'information', 'how to', 'help', 'support'],
      avg_resolution_hours: 8,
      escalation_rate: 0.04
    }
  },
  escalationTriggers: {
    sla_breach: true,
    high_priority_long_wait: true,
    customer_tier_violation: true,
    multiple_responses_no_resolution: true,
    negative_sentiment: true
  }
};

// Advanced ticket categorization using AI-like pattern matching
function categorizeTicket(subject, description) {
  const fullText = `${subject} ${description}`.toLowerCase();
  let bestMatch = { category: 'General Inquiry', confidence: 0 };

  Object.entries(supportConfig.categoryPatterns).forEach(([category, config]) => {
    let matchScore = 0;
    config.keywords.forEach(keyword => {
      if (fullText.includes(keyword)) {
        matchScore += 1;
      }
    });

    const confidence = matchScore / config.keywords.length;
    if (confidence > bestMatch.confidence) {
      bestMatch = { category, confidence: Math.round(confidence * 100) };
    }
  });

  return bestMatch;
}

// Calculate SLA compliance and breach detection
function calculateSLAMetrics(ticket) {
  const priority = ticket.priority || 'Medium';
  const slaConfig = supportConfig.slaThresholds[priority] || supportConfig.slaThresholds['Medium'];
  const customerTier = supportConfig.customerTiers[ticket.customer_tier] || supportConfig.customerTiers['Standard'];

  // Adjust SLA based on customer tier
  const adjustedSLA = {
    first_response_minutes: Math.floor(slaConfig.first_response_minutes * customerTier.escalation_threshold),
    resolution_hours: Math.floor(slaConfig.resolution_hours * customerTier.escalation_threshold)
  };

  const createdTime = new Date(ticket.created_date);
  const currentTime = new Date();
  const lastUpdatedTime = new Date(ticket.last_updated || ticket.created_date);

  // Calculate time metrics
  const ageInMinutes = (currentTime - createdTime) / (1000 * 60);
  const ageInHours = ageInMinutes / 60;
  const timeSinceLastUpdate = (currentTime - lastUpdatedTime) / (1000 * 60);

  // SLA breach detection
  const slaBreaches = {
    first_response_breach: ticket.response_count === 0 && ageInMinutes > adjustedSLA.first_response_minutes,
    resolution_breach: ticket.status !== 'Closed' && ageInHours > adjustedSLA.resolution_hours,
    update_breach: timeSinceLastUpdate > (adjustedSLA.first_response_minutes * 2)
  };

  const overallSLABreach = Object.values(slaBreaches).some(breach => breach);

  return {
    age_minutes: Math.round(ageInMinutes),
    age_hours: Math.round(ageInHours * 100) / 100,
    time_since_last_update_minutes: Math.round(timeSinceLastUpdate),
    sla_thresholds: adjustedSLA,
    sla_breaches: slaBreaches,
    overall_sla_breach: overallSLABreach,
    sla_status: overallSLABreach ? 'Breached' : 'On Track'
  };
}

// Advanced escalation logic
function calculateEscalationScore(ticket, slaMetrics, categorization) {
  let escalationScore = 0;
  const escalationReasons = [];

  // SLA breach escalation
  if (slaMetrics.overall_sla_breach) {
    escalationScore += 25;
    escalationReasons.push('SLA Breach Detected');
  }

  // Priority-based escalation
  const priorityScores = { 'Critical': 20, 'High': 15, 'Medium': 5, 'Low': 0 };
  escalationScore += priorityScores[ticket.priority] || 0;

  // Customer tier escalation
  const tierScores = { 'Enterprise': 15, 'Premium': 10, 'Standard': 5, 'Basic': 0 };
  escalationScore += tierScores[ticket.customer_tier] || 0;

  // Age-based escalation
  if (slaMetrics.age_hours > 48) {
    escalationScore += 15;
    escalationReasons.push('Ticket Age Exceeds 48 Hours');
  } else if (slaMetrics.age_hours > 24) {
    escalationScore += 10;
    escalationReasons.push('Ticket Age Exceeds 24 Hours');
  }

  // Response pattern analysis
  if (ticket.response_count > 5 && ticket.status !== 'Closed') {
    escalationScore += 12;
    escalationReasons.push('Multiple Responses Without Resolution');
  }

  // Already escalated penalty
  if (ticket.escalated) {
    escalationScore += 20;
    escalationReasons.push('Previously Escalated');
  }

  // Technical complexity
  if (categorization.category === 'Technical' && slaMetrics.age_hours > 8) {
    escalationScore += 8;
    escalationReasons.push('Complex Technical Issue');
  }

  // Determine escalation level
  let escalationLevel = 'None';
  let requiresEscalation = false;

  if (escalationScore >= 50) {
    escalationLevel = 'Critical - Immediate Manager Attention';
    requiresEscalation = true;
  } else if (escalationScore >= 35) {
    escalationLevel = 'High - Team Lead Review';
    requiresEscalation = true;
  } else if (escalationScore >= 20) {
    escalationLevel = 'Medium - Senior Agent Assignment';
    requiresEscalation = true;
  }

  return {
    escalation_score: escalationScore,
    escalation_level: escalationLevel,
    requires_escalation: requiresEscalation,
    escalation_reasons: escalationReasons
  };
}

// Performance analytics calculation
function calculatePerformanceMetrics(tickets) {
  const totalTickets = tickets.length;
  if (totalTickets === 0) return {};

  const closedTickets = tickets.filter(t => t.status === 'Closed');
  const openTickets = tickets.filter(t => t.status !== 'Closed');

  // Resolution time analysis
  const resolutionTimes = closedTickets
    .filter(t => t.resolution_time_hours)
    .map(t => t.resolution_time_hours);

  const avgResolutionTime = resolutionTimes.length > 0 
    ? resolutionTimes.reduce((a, b) => a + b, 0) / resolutionTimes.length 
    : 0;

  // Customer satisfaction analysis
  const satisfactionScores = closedTickets
    .filter(t => t.customer_satisfaction_score)
    .map(t => t.customer_satisfaction_score);

  const avgSatisfactionScore = satisfactionScores.length > 0
    ? satisfactionScores.reduce((a, b) => a + b, 0) / satisfactionScores.length
    : 0;

  // Category distribution
  const categoryDistribution = {};
  tickets.forEach(ticket => {
    const category = ticket.ai_category || ticket.category || 'Unknown';
    categoryDistribution[category] = (categoryDistribution[category] || 0) + 1;
  });

  return {
    total_tickets: totalTickets,
    open_tickets: openTickets.length,
    closed_tickets: closedTickets.length,
    resolution_rate: Math.round((closedTickets.length / totalTickets) * 100),
    avg_resolution_time_hours: Math.round(avgResolutionTime * 100) / 100,
    avg_satisfaction_score: Math.round(avgSatisfactionScore * 100) / 100,
    category_distribution: categoryDistribution,
    escalation_rate: Math.round((tickets.filter(t => t.requires_escalation).length / totalTickets) * 100)
  };
}

// Process each input (open tickets, closed tickets, knowledge base)
inputData.forEach(input => {
  if (input.json.result && input.json.result.tickets) {
    const platform = input.json.result.platform || 'Support System';

    input.json.result.tickets.forEach(ticket => {
      const categorization = categorizeTicket(ticket.subject, ticket.description);
      const slaMetrics = calculateSLAMetrics(ticket);
      const escalationAnalysis = calculateEscalationScore(ticket, slaMetrics, categorization);

      processedTickets.push({
        json: {
          // Ticket Identification
          ticket_id: ticket.ticket_id,
          platform: platform,
          status: ticket.status,
          created_date: ticket.created_date,
          last_updated: ticket.last_updated,
          closed_date: ticket.closed_date,

          // Customer Information
          customer_name: ticket.customer_name,
          customer_email: ticket.customer_email,
          customer_tier: ticket.customer_tier || 'Standard',

          // Ticket Details
          subject: ticket.subject,
          description: ticket.description,
          priority: ticket.priority || 'Medium',
          original_category: ticket.category,
          ai_category: categorization.category,
          ai_categorization_confidence: categorization.confidence,

          // Agent and Assignment
          assigned_agent: ticket.assigned_agent,
          response_count: ticket.response_count || 0,

          // Time and SLA Metrics
          age_minutes: slaMetrics.age_minutes,
          age_hours: slaMetrics.age_hours,
          time_since_last_update_minutes: slaMetrics.time_since_last_update_minutes,
          sla_status: slaMetrics.sla_status,
          sla_breach: slaMetrics.overall_sla_breach,
          first_response_time_minutes: ticket.first_response_time_minutes,
          resolution_time_hours: ticket.resolution_time_hours,

          // Escalation Analysis
          escalation_score: escalationAnalysis.escalation_score,
          escalation_level: escalationAnalysis.escalation_level,
          requires_escalation: escalationAnalysis.requires_escalation,
          escalation_reasons: escalationAnalysis.escalation_reasons,
          previously_escalated: ticket.escalated || false,

          // Performance Metrics
          customer_satisfaction_score: ticket.customer_satisfaction_score,
          resolution_summary: ticket.resolution_summary,

          // Additional Context
          tags: ticket.tags || [],
          estimated_resolution_time: ticket.estimated_resolution_time,

          // Analytics Metadata
          processed_at: new Date().toISOString(),
          analytics_session: `analytics_${new Date().toISOString().split('T')[0].replace(/-/g, '')}`
        }
      });
    });
  }

  // Process knowledge base data
  if (input.json.result && input.json.result.articles) {
    const kbAnalytics = {
      total_articles: input.json.result.articles.length,
      high_traffic_articles: input.json.result.articles.filter(a => a.views_count > 1000).length,
      self_service_opportunities: input.json.result.articles.filter(a => a.related_tickets_count > 20).length
    };

    analyticsData.push({
      json: {
        analytics_type: 'knowledge_base',
        data: kbAnalytics,
        processed_at: new Date().toISOString()
      }
    });
  }
});

// Generate overall performance analytics
if (processedTickets.length > 0) {
  const overallMetrics = calculatePerformanceMetrics(processedTickets.map(t => t.json));
  analyticsData.push({
    json: {
      analytics_type: 'performance_summary',
      data: overallMetrics,
      processed_at: new Date().toISOString()
    }
  });
}

// Return both processed tickets and analytics data
return [...processedTickets, ...analyticsData];

