// Extract and validate research parameters
const body = $input.all()[0].json.body;

// Default research configuration
const researchConfig = {
  topic: body.topic || 'artificial intelligence trends',
  depth: body.depth || 'comprehensive', // basic, detailed, comprehensive
  sources: body.sources || ['web', 'academic', 'news'],
  timeframe: body.timeframe || '6months',
  language: body.language || 'en',
  maxSources: body.maxSources || 10,
  analysisType: body.analysisType || 'summary' // summary, detailed, comparative
};

// Generate search queries based on topic
const baseQueries = [
  `${researchConfig.topic} latest developments`,
  `${researchConfig.topic} research findings`,
  `${researchConfig.topic} market analysis`,
  `${researchConfig.topic} expert opinions`,
  `${researchConfig.topic} case studies`
];

// Add specific queries based on depth
if (researchConfig.depth === 'comprehensive') {
  baseQueries.push(
    `${researchConfig.topic} academic papers`,
    `${researchConfig.topic} industry reports`,
    `${researchConfig.topic} statistical data`,
    `${researchConfig.topic} future predictions`
  );
}

return [{
  json: {
    ...researchConfig,
    searchQueries: baseQueries,
    timestamp: new Date().toISOString(),
    sessionId: `research_${Date.now()}`
  }
}];

// Get current batch data
const items = $input.all();
const currentItem = items[0].json;
const queries = currentItem.searchQueries;
const currentBatch = $('Split Search Queries').item.json;

// Get current query
const currentQuery = queries[currentBatch.index];

return [{
  json: {
    ...currentItem,
    currentQuery: currentQuery,
    batchIndex: currentBatch.index
  }
}];

// Combine and process all research data
const allItems = $input.all();
const researchData = allItems[0].json;
const newsData = allItems[1]?.json || {};
const academicData = allItems[2]?.json || {};

// Extract and structure the research findings
const processedData = {
  sessionId: researchData.sessionId,
  query: researchData.currentQuery,
  batchIndex: researchData.batchIndex,
  timestamp: new Date().toISOString(),

  // General research findings
  generalFindings: {
    title: researchData.result?.title || 'Research Findings',
    summary: researchData.result?.summary || '',
    keyPoints: researchData.result?.keyPoints || [],
    statistics: researchData.result?.statistics || [],
    credibilityScore: researchData.result?.credibilityScore || 0
  },

  // News findings
  newsFindings: {
    articles: newsData.result?.articles || [],
    totalArticles: newsData.result?.articles?.length || 0
  },

  // Academic findings
  academicFindings: {
    papers: academicData.result?.papers || [],
    totalPapers: academicData.result?.papers?.length || 0
  },

  // Meta information
  sourceTypes: ['general', 'news', 'academic'],
  totalSources: (researchData.result?.sources?.length || 0) + 
                (newsData.result?.articles?.length || 0) + 
                (academicData.result?.papers?.length || 0)
};

return [{
  json: processedData
}];

