const items = $input.all();
const grouped = {};

// Group items by domain
for (const item of items) {
  const url = item.json.url;
  const domain = url.split("/")[2]; // Extract domain
  item.json.domain = domain;

  if (!grouped[domain]) {
    grouped[domain] = [];
  }

  grouped[domain].push(item.json); // Store just the data part
}

// Convert grouped object to n8n-friendly format
const result = [];

for (const group of Object.values(grouped)) {
  result.push({

      group: group, // Now `group` is the array under an object

  });
}

return result;

