const prompts = [
  $json.prompt1,
  $json.prompt2,
  $json.prompt3,
  $json.prompt4,
  $json.prompt5,
  $json.prompt6
];
const randomIndex = Math.floor(Math.random() * prompts.length);
return [{ json: { selectedPrompt: prompts[randomIndex] } }];

const times = ['09:00', '11:00', '15:00']; // Central Time Options
const selectedTime = times[Math.floor(Math.random() * times.length)];

// Extract hours and minutes
const [hour, minute] = selectedTime.split(':').map(Number);

// Get current date
const now = new Date();

// Create a UTC Date corresponding to selected Central Time
// Central is UTC-5
const postDateUTC = new Date(Date.UTC(
    now.getFullYear(),
    now.getMonth(),
    now.getDate(),
    hour + 5, // Convert from Central to UTC
    minute,
    0,
    0
));

return [{
    json: {
        postTimeCentral: selectedTime,
        postTimeUTC_ISO: postDateUTC.toISOString()
    }
}];

