const now = new Date();
const utcDay = now.getUTCDay(); // Sunday = 0, Saturday = 6

// Convert UTC day to Central time (-5 hours)
const utcHour = now.getUTCHours();
const centralHour = utcHour - 5;

// If converting to previous day (before midnight Central)
let centralDay = utcDay;
if (centralHour < 0) {
  centralDay = utcDay - 1;
  if (centralDay < 0) centralDay = 6; // Wrap Sunday (0) back to Saturday (6)
}

const skipDays = [0, 5, 6]; // Sunday, Friday, Saturday
console.log('UTC Day:', utcDay);
console.log('Central Day:', centralDay);
return [{ json: { skipToday: skipDays.includes(centralDay) } }];
