const inputData = $input.all();
const outputItems = [];

for (const item of inputData) {
  if (item.json.records && Array.isArray(item.json.records)) {

    for (const record of item.json.records) {
      if (record.fields?.Thumbnail?.[0]?.url) {
        const thumbnail = record.fields.Thumbnail[0];

        // W√§hle die beste Thumbnail-Qualit√§t
        let thumbnailUrl = thumbnail.url;
        if (thumbnail.thumbnails?.large?.url) {
          thumbnailUrl = thumbnail.thumbnails.large.url;
        } else if (thumbnail.thumbnails?.full?.url) {
          thumbnailUrl = thumbnail.thumbnails.full.url;
        }

        outputItems.push({
          json: {
            // Original Record Info
            recordId: record.id,
            fileName: record.fields['File Name'] || 'Unknown',
            mediaType: record.fields['Media Type'] || 'Unknown',
            uploadDate: record.fields['Upload Date'],
            fileSize: record.fields['File Size'],

            // Thumbnail Info
            originalThumbnailUrl: thumbnailUrl,
            thumbnailId: thumbnail.id,

            // Processing Status
            step: 'ready_for_bg_removal',
            processedAt: new Date().toISOString(),

            // F√ºr Dateiname-Generierung
            cleanFileName: (record.fields['File Name'] || 'unknown')
              .replace(/[^a-zA-Z0-9]/g, '_')
              .toLowerCase()
          }
        });
      }
    }
  }
}

console.log(`${outputItems.length} Thumbnails f√ºr Verarbeitung vorbereitet`);
return outputItems;
