// Split input: open deals and closed deals
const openDeals = items.filter(item => item.json['Status'] !== 'Won' && item.json['Status'] !== 'Lost');
const closedDeals = items.filter(item => item.json['Status'] === 'Won');

// Metrics
const pipelineValue = openDeals.reduce((sum, d) => sum + Number(d.json['Value'] || 0), 0);
const topDeal = openDeals.sort((a, b) => b.json['Value'] - a.json['Value'])[0];
const winRate = closedDeals.length + openDeals.length > 0
  ? (closedDeals.length / (closedDeals.length + openDeals.length)) * 100
  : 0;

// Format
return [
  {
    json: {
      openDeals,
      closedDeals,
      summary: `üìä *Weekly Pipeline Report*`,
      value: `‚Ä¢ Open Deals: *${openDeals.length}*  
‚Ä¢ Pipeline Value: *¬£${pipelineValue.toLocaleString()}*  
‚Ä¢ Top Deal: *${topDeal?.json['Deal Name']}* ‚Äì ¬£${topDeal?.json['Value']}  
‚Ä¢ Win Rate: *${winRate.toFixed(1)}%*`
    }
  }
];

const openDeals = items[0].json.openDeals;
const closedDeals = items[0].json.closedDeals;

// Extract field helper
const getField = (record, field) => record.json?.[field] ?? null;

const totalPipeline = openDeals.reduce((sum, record) => {
  return sum + (parseFloat(getField(record, 'Value')) || 0);
}, 0);

const weightedPipeline = openDeals.reduce((sum, record) => {
  const stage = getField(record, 'Stage');
  const value = parseFloat(getField(record, 'Value')) || 0;

  const stageWeights = {
    'Qualified': 0.2,
    'Proposal Sent': 0.4,
    'Negotiation': 0.6,
    'Won': 1,
    'Lost': 0
  };

  const weight = stageWeights[stage] || 0.2;
  return sum + (value * weight);
}, 0);

const totalClosed = closedDeals.reduce((sum, record) => {
  return sum + (parseFloat(getField(record, 'Value')) || 0);
}, 0);

const winRate = openDeals.length + closedDeals.length > 0
  ? ((closedDeals.length / (openDeals.length + closedDeals.length)) * 100).toFixed(1)
  : 'N/A';

return [
  {
    json: {
      totalPipeline: `¬£${totalPipeline.toLocaleString()}`,
      weightedPipeline: `¬£${weightedPipeline.toLocaleString()}`,
      totalClosed: `¬£${totalClosed.toLocaleString()}`,
      winRate: `${winRate}%`,
      openDealsCount: openDeals.length,
      closedDealsCount: closedDeals.length
    }
  }
];
