// Simplified message parsing
const message = $input.first().json?.message?.text;

if (!message) {
  return { event_name: '', notes: 'No message received' };
}

const colonIndex = message.indexOf(':');
if (colonIndex === -1) {
  return { event_name: '', notes: message.trim() };
}

return {
  event_name: message.substring(0, colonIndex).trim(),
  notes: message.substring(colonIndex + 1).trim()
};

// Improved event matching with fuzzy search
const allData = $input.all();

// Get parsed message data
const parseData = allData.find(item => 
  item.json?.event_name !== undefined
)?.json;

if (!parseData) {
  return { error: "No parsed message data found" };
}

// Get calendar events
const calendarEvents = allData.filter(item => 
  item.json?.summary && item.json?.start
);

// Enhanced matching function
function findBestMatch(searchTerm, events) {
  const search = searchTerm.toLowerCase();

  // Exact match first
  let match = events.find(e => e.json.summary.toLowerCase() === search);
  if (match) return match.json;

  // Contains match
  match = events.find(e => e.json.summary.toLowerCase().includes(search));
  if (match) return match.json;

  // Reverse contains (search term contains event name)
  match = events.find(e => search.includes(e.json.summary.toLowerCase()));
  if (match) return match.json;

  // Word-based matching
  const searchWords = search.split(/\s+/);
  match = events.find(e => {
    const eventTitle = e.json.summary.toLowerCase();
    return searchWords.some(word => eventTitle.includes(word) && word.length > 2);
  });

  return match?.json;
}

const matchedEvent = findBestMatch(parseData.event_name, calendarEvents);

if (matchedEvent) {
  return {
    event_found: true,
    event_title: matchedEvent.summary,
    event_date: matchedEvent.start.dateTime || matchedEvent.start.date,
    event_timezone: matchedEvent.start.timeZone || 'Europe/Berlin',
    event_location: matchedEvent.location || 'No location specified',
    event_description: matchedEvent.description || '',
    event_attendees: matchedEvent.attendees?.map(a => a.email).join(', ') || 'No attendees listed',
    personal_notes: parseData.notes,
    event_id: matchedEvent.id
  };
} else {
  return {
    event_found: false,
    event_title: parseData.event_name,
    event_date: 'Event not found in calendar',
    event_timezone: 'Unknown',
    event_location: 'Unknown',
    event_description: '',
    event_attendees: '',
    personal_notes: parseData.notes,
    event_id: null
  };
}

const data = $json;

const prompt = `Transform this event information and personal notes into an engaging LinkedIn post:

Event: ${data.event_title}
Date: ${data.event_date}
Location: ${data.event_location}
My Personal Notes: ${data.personal_notes}

Create a professional LinkedIn post that highlights key insights and takeaways from this event. Include relevant hashtags and make it engaging for my professional network.`;

// Return both the prompt and preserve the original data
return {
  chatInput: prompt,
  // Preserve all original data
  event_title: data.event_title,
  event_date: data.event_date,
  event_location: data.event_location,
  personal_notes: data.personal_notes,
  event_found: data.event_found,
  event_id: data.event_id
};

const data = $input.first().json;

// Format date helper - fixed to prevent 24:xx hour format
function formatDate(dateString) {
  if (!dateString || dateString.includes('not found')) return 'Unknown';
  try {
    const date = new Date(dateString);
    const year = date.getFullYear();
    const month = String(date.getMonth() + 1).padStart(2, '0');
    const day = String(date.getDate()).padStart(2, '0');
    const hours = String(date.getHours()).padStart(2, '0');
    const minutes = String(date.getMinutes()).padStart(2, '0');
    return `${month}/${day}/${year}, ${hours}:${minutes}`;
  } catch {
    return dateString;
  }
}

// Clean text helper
function cleanText(text) {
  return String(text || '')
    .replace(/\\n/g, ' ')
    .replace(/\n/g, ' ')
    .replace(/\s+/g, ' ')
    .trim();
}

// Get current date with guaranteed correct format
const now = new Date();
const currentDate = `${String(now.getMonth() + 1).padStart(2, '0')}/${String(now.getDate()).padStart(2, '0')}/${now.getFullYear()}, ${String(now.getHours()).padStart(2, '0')}:${String(now.getMinutes()).padStart(2, '0')}`;

// Return formatted data
return {
  'Event Date': formatDate(data.event_date),
  'Event Title': data.event_title || 'Unknown Event',
  'Location': data.event_location || 'Unknown Location',
  'Personal Notes': cleanText(data.personal_notes),
  'LinkedIn Post': cleanText(data.output),
  'Created Date': currentDate
};

