parameters.jsCode:
// Loop over input items and calculate pricing for each booking
for (const item of $input.all()) {
  // Base pricing untuk setiap jenis makeup
  const basePrice = {
    'Yudisium': 200000,
    'Wisuda': 200000,
    'Pendamping': 250000,
    'Bridesmaid': 200000,
    'Tunangan': 200000,
    'Photoshoot': 200000,
    'Prewedding': 200000,
    'Party': 200000
  };

  const makeupType = item.json.makeupType;
  const serviceType = item.json.serviceType || '';
  const addOns = item.json.addOns || '';
  const paymentType = item.json.paymentType || '';

  // Format nomor WhatsApp - ubah 0 di awal menjadi 62
  let formattedWhatsapp = item.json.whatsappNumber || '';
  if (formattedWhatsapp.startsWith('0')) {
    formattedWhatsapp = '62' + formattedWhatsapp.substring(1);
  }
  // Hapus karakter non-numeric (spasi, dash, dll)
  formattedWhatsapp = formattedWhatsapp.replace(/[^0-9]/g, '');

  // Update nomor WhatsApp yang sudah diformat
  item.json.whatsappNumber = formattedWhatsapp;
  item.json.formattedWhatsapp = formattedWhatsapp;

  // Hitung base price berdasarkan jenis makeup
  let totalPrice = basePrice[makeupType] || 200000;

  // Tambah biaya transport untuk homeservice
  if (serviceType.toLowerCase().includes('homeservice')) {
    totalPrice += 50000; // Transport fee Rp 50,000
  }

  // Tambah biaya add-ons jika ada (10% dari base price)
  if (addOns && addOns.trim() !== '') {
    const addOnFee = Math.round(basePrice[makeupType] * 0.1);
    totalPrice += addOnFee;
    item.json.addOnFee = addOnFee;
  } else {
    item.json.addOnFee = 0;
  }

  // Logic untuk DP - jika payment type mengandung "DP" atau "Booking", set amount ke 50000
  let invoiceAmount = totalPrice;
  let isDP = false;

  if (paymentType.toLowerCase().includes('dp') || 
      paymentType.toLowerCase().includes('booking')) {
    invoiceAmount = 50000;
    isDP = true;
  }

  // Format currency untuk display
  const formattedPrice = new Intl.NumberFormat('id-ID', {
    style: 'currency',
    currency: 'IDR',
    minimumFractionDigits: 0
  }).format(totalPrice);

  const formattedInvoiceAmount = new Intl.NumberFormat('id-ID', {
    style: 'currency',
    currency: 'IDR',
    minimumFractionDigits: 0
  }).format(invoiceAmount);

  // Format currency tanpa symbol untuk Xendit
  const priceForXendit = invoiceAmount.toString();

  // Tambahkan field pricing ke item
  item.json.basePrice = basePrice[makeupType] || 200000;
  item.json.transportFee = serviceType.toLowerCase().includes('homeservice') ? 50000 : 0;
  item.json.totalPrice = totalPrice;
  item.json.formattedPrice = formattedPrice;
  item.json.invoiceAmount = invoiceAmount;
  item.json.formattedInvoiceAmount = formattedInvoiceAmount;
  item.json.priceForXendit = priceForXendit;
  item.json.isDP = isDP;

  // Generate unique external ID untuk Xendit
  item.json.externalId = `MUA_${item.json.bookingId.replace('BOOK_', '')}`;

  // Generate invoice description dengan indikator DP jika applicable
  const dpIndicator = isDP ? ' (DP)' : '';
  item.json.invoiceDescription = `Makeup ${makeupType} - ${item.json.customerName} (${item.json.eventDate} ${item.json.eventTime})${dpIndicator}`;

  // Calculate expiry time (24 hours from now)
  const expiryTime = new Date();
  expiryTime.setHours(expiryTime.getHours() + 24);
  item.json.invoiceExpiry = expiryTime.toISOString();

  // Jika DP, hitung sisa pembayaran
  if (isDP) {
    item.json.remainingPayment = totalPrice - invoiceAmount;
    item.json.formattedRemainingPayment = new Intl.NumberFormat('id-ID', {
      style: 'currency',
      currency: 'IDR',
      minimumFractionDigits: 0
    }).format(item.json.remainingPayment);
  }
}
return $input.all();

