// UUID generator from seed
const generateUuidFromSeed = (seed) => {
  const base = seed + Math.random().toString(16);
  const hash = Array.from(base).reduce((hash, char) => {
    return ((hash << 5) - hash) + char.charCodeAt(0);
  }, 0);
  const hex = (hash >>> 0).toString(16).padStart(8, '0');
  return (
    hex.slice(0, 8) + '-' +
    hex.slice(0, 4) + '-' +
    '4' + hex.slice(4, 3) + '-' +
    ((parseInt(hex[0], 16) & 0x3) | 0x8).toString(16) + hex.slice(1, 3) + '-' +
    hex.padEnd(12, '0')
  );
};

// Known transaction type classifications
const expenseTypes = new Set([
  'Expense', 'Bill', 'Check', 'Credit Card Charge', 'Purchase', 'Vendor Credit', 'Bank Fee',
]);

const incomeTypes = new Set([
  'Invoice', 'Payment', 'Deposit', 'Sales Receipt', 'Credit Memo', 'Refund',
]);

const internalTransferAccounts = new Set([
  //FILL HERE
]);

const expenseCategories = new Set([
  //FILL HERE
]);

const incomeCategories = new Set([
  //FILL HERE
]);

const output = [];

for (const item of $input.all()) {
  const report = item.json;
  const weekStartDate = report.Header?.StartPeriod || null;
  const currency = report.Header?.Currency || null;

  if (report.Rows && report.Rows.Row) {
    for (const row of report.Rows.Row) {
      if (row.type && row.type !== 'Data') continue;

      const cols = row.ColData;
      if (!cols || cols.length < 9) continue;

      const txnType = cols[1]?.value || '';
      const splitAccount = cols[7]?.value || null;

      let amountRaw = cols[8]?.value || '0';
      let amountNum = parseFloat(amountRaw);
      if (isNaN(amountNum)) amountNum = 0;

      const seed = [
        cols[0]?.value || '',
        txnType,
        cols[4]?.value || '',
        amountNum.toString()
      ].join('|');

      // Default classifications
      let isExpense = false;
      let isIncome = false;

      // Priority: category-based override
      if (expenseCategories.has(splitAccount)) {
        isExpense = true;
        isIncome = false;
      } else if (incomeCategories.has(splitAccount)) {
        isIncome = true;
        isExpense = false;
      } else if (txnType === "Transfer") {
        if (internalTransferAccounts.has(splitAccount)) {
          isExpense = false;
          isIncome = false;
        } else {
          isExpense = amountNum < 0;
          isIncome = amountNum > 0;
        }
      } else {
        isExpense = amountNum < 0 || expenseTypes.has(txnType);
        isIncome = incomeTypes.has(txnType) && !isExpense;
      }

      const amount = Math.abs(amountNum);
      const amountSigned = isExpense ? -amount : amount;

      let amount_usd = 0;
      let amount_cad = 0;
      if (currency === 'CAD') {
        amount_cad = amount;
      } else if (currency === 'USD') {
        amount_usd = amount;
      }

      const record = {
        id: generateUuidFromSeed(seed),
        txn_date: cols[0]?.value || null,
        txn_type: txnType,
        doc_num: cols[2]?.value || null,
        is_posted: cols[3]?.value === "Yes",
        name: cols[4]?.value || null,
        memo: cols[5]?.value || null,
        account_name: cols[6]?.value || null,
        account_id: cols[6]?.id || null,
        category: splitAccount,
        category_id: cols[7]?.id || null,
        signed_amount: amountSigned,
        is_expense: isExpense,
        is_income: isIncome,
        amount_usd: amount_usd,
        amount_cad: amount_cad,
        currency: currency,
        week_start_date: weekStartDate,
        source_id: cols[1]?.id || ''
      };

      output.push({ json: record });
    }
  }
}

return output;

const formatValue = (val) => {
  if (val === null || val === undefined) return 'NULL';
  if (typeof val === 'number') return val;
  if (typeof val === 'boolean') return val ? 'TRUE' : 'FALSE';

  if (typeof val === 'string') {
    const clean = val
      .replace(/[\u2018\u2019\u201A\u201B\u2032\u2035]/g, '')    // remove smart single quotes
      .replace(/[\u201C\u201D\u201E\u201F\u2033\u2036]/g, '"')   // smart double quotes → "
      .normalize('NFKC')                                        // normalize accents/compatibility
      .replace(/[^\x00-\x7F]/g, '')                             // strip non-ASCII
      .replace(/\?/g, '')                                      // remove question marks
      .replace(/'/g, '')                                       // remove apostrophes
      .replace(/\n/g, ' ')                                     // replace line breaks with space
      .replace(/\r/g, '')                                      // remove carriage returns
      .replace(/\t/g, ' ')                                     // replace tabs with space
      .replace(/\\/g, '');                                     // remove literal backslashes
    return `'${clean}'`;
  }

  // Catch-all stringify fallback
  return `'${JSON.stringify(val).replace(/'/g, '')}'`;
};


const formatDate = (val) => {
  if (!val) return 'NULL';
  return `DATE '${val}'`;
};

const rows = $input.all().map(item => {
  const d = item.json;

  return `(
    ${formatValue(d.id)},
    ${formatValue(d.source_id)},
    ${formatDate(d.txn_date || d.date)},
    ${formatValue(d.txn_type || d.type)},
    ${formatValue(d.doc_num)},
    ${formatValue(d.is_posted)},
    ${formatValue(d.name)},
    ${formatValue(d.memo)},
    ${formatValue(d.account_name)},
    ${formatValue(d.category)},
    ${d.amount_usd ?? 'NULL'},
    ${formatValue(d.is_expense)},
    ${formatValue(d.is_income)},
    ${formatDate(d.week_start_date)},
    ${d.amount_cad ?? 'NULL'},
    ${formatValue(d.account_id)},
    ${formatValue(d.category_id)},
    ${d.signed_amount ?? 'NULL'},
    ${formatValue(d.currency)}
  )`;
});

return [
  {
    json: {
      valuesString: rows.join(',\n')
    }
  }
];
