const order = items[0].json.body;
const customer = order.customer || {};
const address = customer.default_address || order.billing_address || {};
const firstLineItem = order.line_items?.[0] || {};

function extractIdFromGid(gid) {
  return gid ? gid.split('/').pop() : '';
}

function formatDate(dateStr) {
  return dateStr ? new Date(dateStr).toISOString().split('T')[0] : '';
}

function calculateDaysSince(dateStr) {
  if (!dateStr) return '';
  try {
    const diff = new Date() - new Date(dateStr);
    return Math.floor(diff / (1000 * 60 * 60 * 24));
  } catch {
    return '';
  }
}

function calculateAvgDays(firstDate, lastDate, totalOrders) {
  if (!firstDate || !lastDate || totalOrders < 2) return '';
  try {
    const start = new Date(firstDate);
    const end = new Date(lastDate);
    const diffDays = (end - start) / (1000 * 60 * 60 * 24);
    return (diffDays / (totalOrders - 1)).toFixed(1); // Rounded to 1 decimal
  } catch {
    return '';
  }
}

// Extract order dates
const firstOrderDate = formatDate(order.created_at); // Adjust as needed
const lastOrderDate = formatDate(order.created_at);  // You may update this later

const totalOrders = 1; // For webhook use, we assume it's the first or increment later

const output = {
  // Basic Customer Info
  customer_id: customer.id || '',
  first_name: customer.first_name || '',
  last_name: customer.last_name || '',
  email: customer.email || '',
  phone: customer.phone || address.phone || '',

  // Address Info
  address: [
    address.address1,
    address.address2,
    address.city,
    address.province || address.state,
    address.country,
    address.zip
  ].filter(Boolean).join(', '),

  address1: address.address1 || '',
  address2: address.address2 || '',
  city: address.city || '',
  province: address.province || '',
  zip: address.zip || '',
  country: address.country || '',
  country_name: address.country_name || '',

  // Order Info
  order_id: order.id || '',
  order_name: order.name || '',
  first_order_date: firstOrderDate,
  last_order_date: lastOrderDate,
  total_orders: totalOrders.toString(),
  total_spent: order.total_price || '0.00',
  payment_method: order.gateway || '',
  days_since_last_order: calculateDaysSince(order.created_at),

  // Variant Info
  last_product_id: firstLineItem.product_id || '',
  last_variant_id: firstLineItem.variant_id || '',

  // Calculated Metric
  average_days_between_orders: calculateAvgDays(firstOrderDate, lastOrderDate, totalOrders),

  source: 'webhook:order/create'
};

return [{ json: output }];

