// Try to pull subject from different common locations
const subject =
  $json.subject ||
  $json.Subject ||
  $json.headers?.subject ||
  "No Subject";

// Try to pull body text from common fields
const body =
  $json.body ||
  $json.text ||
  $json.snippet ||
  "No message found.";

// Try to pull "from" field from common sources
const fromHeader =
  $json.from ||
  $json.From ||
  $json.headers?.from ||
  "";

// Initialize name and email placeholders
let senderName = "";
let email = "";

// Extract "Name <email@example.com>" if present
const match = fromHeader.match(/(.*?)<(.+?)>/);
if (match) {
  senderName = match[1].trim();
  email = match[2].trim();
} else {
  // If only email address is provided
  email = fromHeader.trim();
}

// Attempt to extract name from message body like: "I am John Doe from ..."
let extractedName = "";
const nameMatch = body.match(/I am (.*?) from/i);
if (nameMatch) {
  extractedName = nameMatch[1].trim();
}

// Choose final name: prefer extracted from body, else senderName, else fallback
const finalName = extractedName || senderName || "Unknown";

return [{
  json: {
    name: finalName,
    email,
    subject,
    message: body,
    timestamp: new Date().toISOString()
  }
}];
