const threads = {};

for (const item of items) {
  const msg = item.json;
  const threadId = msg.threadId;

  if (!threads[threadId]) {
    threads[threadId] = [];
  }

  // Add To and From Addresses
  let finalMessage = "From: " + msg.headers.from;
  finalMessage += "To: " + msg.headers.to;
  finalMessage += "Message: " + msg.textAsHtml

  threads[threadId].push(finalMessage);
}

return Object.keys(threads).map(threadId => ({
  json: {
    threadId,
    chatInput: threads[threadId],
  }
}));
