parameters.inputSchema:
{
  "type": "object",
  "properties": {
    "routine": {
      "type": "object",
      "properties": {
        "title": {
          "type": "string",
          "description": "The title of the routine"
        },
        "folder_id": {
          "type": "number",
          "nullable": true,
          "default": 1352963,
          "description": "The folder id the routine should be added to. Pass null to insert the routine into default 'My Routines' folder"
        },
        "notes": {
          "type": "string",
          "description": "Additional notes for the routine"
        },
        "exercises": {
          "type": "array",
          "description": "List of exercises in the routine",
          "items": {
            "type": "object",
            "properties": {
              "exercise_template_id": {
                "type": "string",
                "description": "The ID of the exercise template"
              },
              "superset_id": {
                "type": "integer",
                "nullable": true,
                "description": "The ID of the superset"
              },
              "rest_seconds": {
                "type": "integer",
                "nullable": true,
                "description": "The rest time in seconds (Pause). Never negative."
              },
              "notes": {
                "type": "string",
                "nullable": true,
                "description": "Additional notes for the exercise"
              },
              "sets": {
                "type": "array",
                "description": "Sets for the exercise",
                "items": {
                  "type": "object",
                  "properties": {
                    "type": {
                      "type": "string",
                      "enum": ["warmup", "normal", "failure", "dropset"],
                      "description": "The type of the set"
                    },
                    "weight_kg": {
                      "type": "number",
                      "nullable": true,
                      "description": "The weight in kilograms"
                    },
                    "reps": {
                      "type": "integer",
                      "nullable": true,
                      "description": "The number of repetitions (wdhl.). Never negative."
                    },
                    "distance_meters": {
                      "type": "integer",
                      "nullable": true,
                      "description": "The distance in meters"
                    },
                    "duration_seconds": {
                      "type": "integer",
                      "nullable": true,
                      "description": "The duration in seconds"
                    },
                    "custom_metric": {
                      "type": "number",
                      "nullable": true,
                      "description": "A custom metric for the set. Currently used for steps and floors"
                    }
                  }
                }
              }
            },
            "required": ["exercise_template_id", "sets"]
          }
        }
      },
      "required": ["title", "exercises"]
    }
  },
  "required": ["routine"]
}

