// Определяем заголовки CSV один раз, они будут одинаковыми для всех строк.
const header = "ad_name,ad_id,spend,impressions,clicks,app_installs,registrations,purchases,purchase_value";

// Используем .map() для преобразования КАЖДОГО входящего элемента
// Метод .map() вернет новый массив той же длины, что и items
return items.map(item => {
  // Внутри .map() мы работаем с одним 'item' за раз.
  // Получаем его json-содержимое
  const currentJson = item.json;

  // Извлекаем данные для ТЕКУЩЕГО элемента, создавая строку со значениями
  const row = [
    `"${currentJson.name.replace(/"/g, '""')}"`, // Экранируем кавычки
    currentJson.ad_id || 0,
    currentJson.spend || 0,
    currentJson.impressions || 0,
    currentJson.clicks || 0,
    currentJson.actions?.find(a => a.action_type.includes('app_install'))?.value || 0,
    currentJson.actions?.find(a => a.action_type.includes('complete_registration'))?.value || 0,
    currentJson.actions?.find(a => a.action_type.includes('purchase'))?.value || 0,
    currentJson.action_values?.find(a => a.action_type.includes('purchase'))?.value || 0
  ].join(',');

  // Возвращаем новую структуру для ТЕКУЩЕГО элемента.
  // Она будет содержать все старые данные + новое поле с CSV.
  return {
    json: {
      ...currentJson, // Сохраняем все исходные данные
      creative_csv_data: `${header}\n${row}` // Добавляем новое поле с CSV
    }
  };
});

