const coin = items[0].json;

function clean(value, decimals = null) {
  if (value === undefined || value === null) {
    return "Belirlenemedi";
  }
  if (typeof value === "number" && decimals !== null) {
    return value.toFixed(decimals);
  }
  return value;
}

return [
  {
    json: {
      name: clean(coin.name),
      symbol: clean(coin.symbol?.toUpperCase()),
      current_price: clean(coin.current_price, 8) + " TL",
      high_24h: clean(coin.high_24h),
      low_24h: clean(coin.low_24h),
      price_change_percentage_24h: clean(coin.price_change_percentage_24h, 2) + "%",
      total_volume: clean(coin.total_volume, 2) + " TL",
      ath: clean(coin.ath, 8),
      ath_change_percentage: clean(coin.ath_change_percentage, 2),
      atl: clean(coin.atl, 8),
      atl_change_percentage: clean(coin.atl_change_percentage, 2),
      circulating_supply: clean(coin.circulating_supply),
      total_supply: clean(coin.total_supply),
      max_supply: clean(coin.max_supply)
    }
  }
];

