// UUID generator from seed
const generateUuidFromSeed = (seed) => {
  const base = seed + Math.random().toString(16);
  const hash = Array.from(base).reduce((hash, char) => {
    return ((hash << 5) - hash) + char.charCodeAt(0);
  }, 0);
  const hex = (hash >>> 0).toString(16).padStart(8, '0');
  return (
    hex.slice(0, 8) + '-' +
    hex.slice(0, 4) + '-' +
    '4' + hex.slice(4, 3) + '-' +
    ((parseInt(hex[0], 16) & 0x3) | 0x8).toString(16) + hex.slice(1, 3) + '-' +
    hex.padEnd(12, '0')
  );
};

const input = $input.all();
const output = [];

for (const item of input) {
  const queryResponse = item.json.QueryResponse;
  if (!queryResponse || !queryResponse.Account) continue;

  // Extract date_created_at from top-level "time" field
  let dateCreatedAt = null;
  if (item.json.time) {
    dateCreatedAt = item.json.time.slice(0, 10); // YYYY-MM-DD
  }

  for (const account of queryResponse.Account) {
    const seed = `${account.Id}|${account.Name}`;
    const id = generateUuidFromSeed(seed);

    // Set balances based on origin currency
    let currentBalanceUsd = 0;
    let currentBalanceCad = 0;

    if (account.CurrencyRef?.value === 'USD') {
      currentBalanceUsd = account.CurrentBalance;
    } else if (account.CurrencyRef?.value === 'CAD') {
      currentBalanceCad = account.CurrentBalance;
    }

    const record = {
      id: id,
      account_id: account.Id || null,
      date_created_at: dateCreatedAt,
      name: account.Name || null,
      active: account.Active || null,
      classification: account.Classification || null,
      account_type: account.AccountType || null,
      current_balance_usd: currentBalanceUsd,
      current_balance_cad: currentBalanceCad,
      currency_origin: account.CurrencyRef?.value || null,
      domain: account.domain || null,
      account_create_time: account.MetaData?.CreateTime || null,
      account_last_update_time: account.MetaData?.LastUpdatedTime || null,
    };

    output.push({ json: record });
  }
}

return output;

const formatValue = (val) => {
  if (typeof val === 'number') return val;
  if (typeof val === 'boolean') return val ? 'TRUE' : 'FALSE';
  if (typeof val === 'string') {
    const clean = val
      .replace(/[\u2018\u2019\u201A\u201B\u2032\u2035]/g, '')   // remove smart single quotes
      .replace(/[\u201C\u201D\u201E\u201F\u2033\u2036]/g, '"')  // smart double quotes to plain
      .normalize('NFKC')                                        // normalize unicode
      .replace(/[^\x00-\x7F]/g, '')                             // strip non-ASCII
      .replace(/\?/g, '')                                      // remove ?
      .replace(/'/g, '')                                       // ðŸ’¥ remove all apostrophes
      .replace(/\n/g, ' ')                                     // replace newlines
      .replace(/\r/g, '')                                      // remove carriage returns
      .replace(/\t/g, ' ')                                     // replace tabs
      .replace(/\\/g, '\\\\');                                 // escape literal backslashes
    return `'${clean}'`;
  }

  if (val === null || val === undefined) return `''`; // empty string instead of NULL
  return `'${JSON.stringify(val).replace(/'/g, '')}'`; // sanitize other cases
};



const formatDate = (val) => {
  if (!val) return 'NULL';
  return `DATE '${val}'`;
};

const rows = $input.all().map(item => {
  const d = item.json;

  return `(
    ${formatValue(d.id)},
    ${formatValue(d.account_id)},
    ${formatDate(d.date_created_at)},
    ${formatValue(d.name)},
    ${formatValue(d.active)},
    ${formatValue(d.classification)},
    ${formatValue(d.account_type)},
    ${d.current_balance_usd ?? 'NULL'},
    ${d.current_balance_cad ?? 'NULL'},
    ${formatValue(d.currency_origin)},
    ${formatValue(d.domain)},
    ${formatValue(d.account_create_time)},
    ${formatValue(d.account_last_update_time)},
    "description"
  )`;
});

return [
  {
    json: {
      valuesString: rows.filter(Boolean).join(',\n') // ensures no comma after the last row
    }
  }
];
