const events = $input.all().map(e => e.json);

// Group events by organizer or creator email
const grouped = {};

for (const event of events) {
  const interviewerEmail = event.organizer?.email || event.creator?.email;
  if (!interviewerEmail) continue;

  if (!grouped[interviewerEmail]) grouped[interviewerEmail] = [];

  const attendees = (event.attendees || []).map(att => {
    return `${att.email} (${att.responseStatus})`;
  }).join('<br>');

  grouped[interviewerEmail].push({
    summary: event.summary || 'No Title',
    start: event.start?.dateTime || event.start?.date || 'N/A',
    end: event.end?.dateTime || event.end?.date || 'N/A',
    description: event.description || 'No description',
    htmlLink: event.htmlLink || '',
    meetLink: event.conferenceData?.entryPoints?.[0]?.uri || event.hangoutLink || 'N/A',
    attendees: attendees || 'No attendees'
  });
}

const output = [];

for (const [email, interviews] of Object.entries(grouped)) {
  let html = `<h3>Your Interview Schedule for Today</h3>
  <table border="1" cellpadding="5" cellspacing="0">
    <tr>
      <th>Title</th>
      <th>Start</th>
      <th>End</th>
      <th>Description</th>
      <th>Meeting Link</th>
      <th>Attendees (Status)</th>
    </tr>`;

  for (const iv of interviews) {
    html += `<tr>
      <td>${iv.summary}</td>
      <td>${new Date(iv.start).toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' })}</td>
      <td>${new Date(iv.end).toLocaleString('en-IN', { timeZone: 'Asia/Kolkata' })}</td>
      <td>${iv.description}</td>
      <td><a href="${iv.meetLink}" target="_blank">Join</a></td>
      <td>${iv.attendees}</td>
    </tr>`;
  }

  html += `</table>`;

  output.push({
    json: {
      interviewer_email: email,
      subject: "Today's Interview Schedule",
      htmlContent: html
    }
  });
}

return output;
