// Get the input data from ScrapeGraphAI
const inputData = $input.all()[0].json;

// Extract regulatory changes array from result
const regulations = inputData.result.regulatory_changes || inputData.result.rules || inputData.result.regulations || inputData.regulations || [];

function parseRegulation(regulation) {
  const {
    title,
    agency,
    publication_date,
    effective_date,
    summary,
    impact_level,
    affected_sectors,
    document_url,
    rule_type,
    comment_deadline
  } = regulation;

  // Clean and validate data
  const cleanTitle = title?.trim() || 'Title not available';
  const cleanAgency = agency?.trim() || 'Agency not specified';
  const cleanSummary = summary?.trim() || 'Summary not available';
  const cleanImpactLevel = impact_level || 'Medium';
  const cleanRuleType = rule_type || 'Unknown';
  const cleanUrl = document_url || '#';

  // Parse dates
  const pubDate = publication_date ? new Date(publication_date).toLocaleDateString() : 'Not specified';
  const effDate = effective_date ? new Date(effective_date).toLocaleDateString() : 'Not specified';
  const commentDate = comment_deadline ? new Date(comment_deadline).toLocaleDateString() : 'N/A';

  // Handle sectors array
  const sectors = Array.isArray(affected_sectors) ? affected_sectors : (affected_sectors ? [affected_sectors] : ['General']);

  return {
    title: cleanTitle,
    agency: cleanAgency,
    publication_date: pubDate,
    effective_date: effDate,
    summary: cleanSummary,
    impact_level: cleanImpactLevel,
    affected_sectors: sectors,
    document_url: cleanUrl,
    rule_type: cleanRuleType,
    comment_deadline: commentDate,
    parsed_at: new Date().toISOString()
  };
}

// Debug log
console.log(`Found ${regulations.length} regulatory changes`);

// Return each regulation as separate execution with parsed data
return regulations.map(regulation => ({
  json: parseRegulation(regulation)
}));

// Get parsed regulation data
const regulation = $input.all()[0].json;

// Define impact assessment criteria
function assessImpact(regulation) {
  const { title, summary, affected_sectors, rule_type, agency } = regulation;

  let impactScore = 0;
  let riskFactors = [];
  let opportunities = [];
  let complianceActions = [];

  // Impact scoring based on rule type
  switch (rule_type.toLowerCase()) {
    case 'final rule':
      impactScore += 3;
      complianceActions.push('Immediate compliance review required');
      break;
    case 'proposed rule':
      impactScore += 2;
      complianceActions.push('Prepare comment response');
      break;
    case 'notice':
      impactScore += 1;
      complianceActions.push('Monitor for developments');
      break;
  }

  // Sector-specific impact assessment
  const criticalSectors = ['financial services', 'banking', 'healthcare', 'energy', 'technology'];
  const matchedCriticalSectors = affected_sectors.filter(sector => 
    criticalSectors.some(critical => sector.toLowerCase().includes(critical.toLowerCase()))
  );

  if (matchedCriticalSectors.length > 0) {
    impactScore += 2;
    riskFactors.push(`Direct impact on critical sectors: ${matchedCriticalSectors.join(', ')}`);
  }

  // Keyword-based impact assessment
  const highImpactKeywords = ['compliance', 'penalty', 'fine', 'mandatory', 'prohibited', 'required'];
  const mediumImpactKeywords = ['guidance', 'recommendation', 'best practice', 'voluntary'];
  const opportunityKeywords = ['incentive', 'tax credit', 'grant', 'funding', 'streamlined'];

  const textToAnalyze = `${title} ${summary}`.toLowerCase();

  highImpactKeywords.forEach(keyword => {
    if (textToAnalyze.includes(keyword)) {
      impactScore += 1;
      riskFactors.push(`Contains high-impact keyword: ${keyword}`);
    }
  });

  mediumImpactKeywords.forEach(keyword => {
    if (textToAnalyze.includes(keyword)) {
      impactScore += 0.5;
    }
  });

  opportunityKeywords.forEach(keyword => {
    if (textToAnalyze.includes(keyword)) {
      opportunities.push(`Potential opportunity: ${keyword}`);
    }
  });

  // Determine final impact level
  let finalImpactLevel;
  if (impactScore >= 5) {
    finalImpactLevel = 'Critical';
    complianceActions.push('Executive review required within 24 hours');
  } else if (impactScore >= 3) {
    finalImpactLevel = 'High';
    complianceActions.push('Legal and compliance team review required');
  } else if (impactScore >= 1.5) {
    finalImpactLevel = 'Medium';
    complianceActions.push('Department head review recommended');
  } else {
    finalImpactLevel = 'Low';
    complianceActions.push('Standard monitoring sufficient');
  }

  return {
    ...regulation,
    impact_assessment: {
      impact_score: impactScore,
      final_impact_level: finalImpactLevel,
      risk_factors: riskFactors,
      opportunities: opportunities,
      compliance_actions: complianceActions,
      assessment_date: new Date().toISOString()
    }
  };
}

// Return assessed regulation
return [{ json: assessImpact(regulation) }];

// Get assessed regulation data
const regulation = $input.all()[0].json;
const { impact_assessment, title, agency, effective_date, comment_deadline } = regulation;

// Create compliance tracking record
function createComplianceTracker(regulation) {
  const compliance = {
    regulation_id: `REG_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
    title: regulation.title,
    agency: regulation.agency,
    status: 'New',
    priority: regulation.impact_assessment.final_impact_level,
    key_dates: {
      publication_date: regulation.publication_date,
      effective_date: regulation.effective_date,
      comment_deadline: regulation.comment_deadline,
      review_due_date: calculateReviewDate(regulation.impact_assessment.final_impact_level)
    },
    assigned_team: assignTeam(regulation.impact_assessment.final_impact_level),
    compliance_tasks: generateComplianceTasks(regulation),
    tracking_status: {
      initial_review: 'Pending',
      impact_analysis: 'Pending',
      policy_update: 'Pending',
      training_required: 'Pending',
      implementation: 'Pending'
    },
    created_at: new Date().toISOString(),
    last_updated: new Date().toISOString()
  };

  return {
    ...regulation,
    compliance_tracking: compliance
  };
}

function calculateReviewDate(impactLevel) {
  const now = new Date();
  switch (impactLevel) {
    case 'Critical':
      return new Date(now.getTime() + 24 * 60 * 60 * 1000).toISOString(); // 1 day
    case 'High':
      return new Date(now.getTime() + 3 * 24 * 60 * 60 * 1000).toISOString(); // 3 days
    case 'Medium':
      return new Date(now.getTime() + 7 * 24 * 60 * 60 * 1000).toISOString(); // 1 week
    default:
      return new Date(now.getTime() + 14 * 24 * 60 * 60 * 1000).toISOString(); // 2 weeks
  }
}

function assignTeam(impactLevel) {
  switch (impactLevel) {
    case 'Critical':
      return ['Executive Team', 'Legal', 'Compliance', 'Operations'];
    case 'High':
      return ['Legal', 'Compliance', 'Department Heads'];
    case 'Medium':
      return ['Compliance', 'Relevant Department'];
    default:
      return ['Compliance'];
  }
}

function generateComplianceTasks(regulation) {
  const baseTasks = [
    'Review regulation text',
    'Assess current policy alignment',
    'Identify compliance gaps'
  ];

  const { final_impact_level, compliance_actions } = regulation.impact_assessment;

  if (final_impact_level === 'Critical' || final_impact_level === 'High') {
    baseTasks.push(
      'Conduct legal review',
      'Update internal policies',
      'Plan staff training',
      'Create implementation timeline'
    );
  }

  if (regulation.comment_deadline !== 'N/A') {
    baseTasks.push('Prepare regulatory comment response');
  }

  return baseTasks.concat(compliance_actions);
}

// Return regulation with compliance tracking
return [{ json: createComplianceTracker(regulation) }];

