// Get input data from webhook and scraped company data
const visitorData = $input.first().json;
const companyData = $input.last().json.result || {};

// Enrich visitor data with company intelligence
function enrichVisitorData(visitor, company) {
  const enrichedData = {
    // Original visitor data
    visitor_id: visitor.visitor_id || generateVisitorId(),
    timestamp: visitor.timestamp || new Date().toISOString(),
    ip_address: visitor.ip_address,
    user_agent: visitor.user_agent,
    page_visited: visitor.page_visited,
    referrer: visitor.referrer,
    session_duration: visitor.session_duration || 0,
    pages_viewed: visitor.pages_viewed || 1,

    // Enriched company data
    company: {
      name: company.company_name || 'Unknown Company',
      domain: visitor.company_domain || visitor.visitor_ip_domain,
      industry: company.industry || 'Unknown',
      size: company.company_size || 'Unknown',
      description: company.description || '',
      services: company.services || [],
      technologies: company.technologies || [],
      funding_info: company.funding_info || '',
      competitors: company.competitors || []
    },

    // Contact information
    contact_info: company.contact_info || {},
    social_media: company.social_media || {},
    key_personnel: company.key_personnel || [],

    // Behavioral data
    behavior: {
      visit_type: determineVisitType(visitor),
      engagement_level: calculateEngagement(visitor),
      intent_signals: identifyIntentSignals(visitor),
      device_type: getDeviceType(visitor.user_agent),
      location: visitor.location || 'Unknown'
    },

    // Recent company activity
    recent_activity: {
      news: company.recent_news || [],
      last_updated: new Date().toISOString()
    }
  };

  return enrichedData;
}

function generateVisitorId() {
  return 'visitor_' + Date.now() + '_' + Math.random().toString(36).substr(2, 9);
}

function determineVisitType(visitor) {
  if (visitor.referrer && visitor.referrer.includes('google.com')) {
    return 'organic_search';
  } else if (visitor.referrer && visitor.referrer.includes('linkedin.com')) {
    return 'social_media';
  } else if (!visitor.referrer) {
    return 'direct';
  } else {
    return 'referral';
  }
}

function calculateEngagement(visitor) {
  const duration = visitor.session_duration || 0;
  const pages = visitor.pages_viewed || 1;

  if (duration > 300 && pages > 3) return 'high';
  if (duration > 120 && pages > 1) return 'medium';
  return 'low';
}

function identifyIntentSignals(visitor) {
  const signals = [];
  const page = visitor.page_visited || '';

  if (page.includes('/pricing')) signals.push('pricing_interest');
  if (page.includes('/demo')) signals.push('demo_request');
  if (page.includes('/contact')) signals.push('contact_intent');
  if (page.includes('/case-study')) signals.push('research_mode');
  if (page.includes('/careers')) signals.push('hiring_interest');

  return signals;
}

function getDeviceType(userAgent) {
  if (!userAgent) return 'unknown';
  if (/Mobile|Android|iPhone/i.test(userAgent)) return 'mobile';
  if (/Tablet|iPad/i.test(userAgent)) return 'tablet';
  return 'desktop';
}

// Process the enrichment
const enrichedVisitor = enrichVisitorData(visitorData, companyData);

console.log('Visitor enriched with company intelligence:', enrichedVisitor.company.name);

return {
  json: enrichedVisitor
};

// Get enriched visitor data
const enrichedData = $input.first().json;

// Lead scoring algorithm
function calculateLeadScore(data) {
  let score = 0;
  const factors = {};

  // Company size scoring (0-25 points)
  const companySize = data.company.size;
  if (companySize === '1000+') {
    score += 25;
    factors.company_size = 25;
  } else if (companySize === '201-1000') {
    score += 20;
    factors.company_size = 20;
  } else if (companySize === '51-200') {
    score += 15;
    factors.company_size = 15;
  } else if (companySize === '1-50') {
    score += 10;
    factors.company_size = 10;
  } else {
    factors.company_size = 0;
  }

  // Industry relevance (0-20 points)
  const targetIndustries = ['Technology', 'SaaS', 'Software', 'Finance', 'Healthcare'];
  if (targetIndustries.includes(data.company.industry)) {
    score += 20;
    factors.industry_fit = 20;
  } else {
    factors.industry_fit = 0;
  }

  // Engagement level (0-20 points)
  if (data.behavior.engagement_level === 'high') {
    score += 20;
    factors.engagement = 20;
  } else if (data.behavior.engagement_level === 'medium') {
    score += 12;
    factors.engagement = 12;
  } else {
    score += 5;
    factors.engagement = 5;
  }

  // Intent signals (0-25 points)
  const intentPoints = data.behavior.intent_signals.length * 5;
  score += Math.min(intentPoints, 25);
  factors.intent_signals = Math.min(intentPoints, 25);

  // Technology stack alignment (0-10 points)
  const ourTechnologies = ['React', 'AWS', 'Python', 'Node.js', 'Docker'];
  const commonTech = data.company.technologies.filter(tech => 
    ourTechnologies.includes(tech)
  ).length;
  const techScore = Math.min(commonTech * 2, 10);
  score += techScore;
  factors.tech_alignment = techScore;

  return { score, factors };
}

// Calculate lead qualification
function qualifyLead(score) {
  if (score >= 80) return 'hot';
  if (score >= 60) return 'warm';
  if (score >= 40) return 'qualified';
  if (score >= 20) return 'cold';
  return 'unqualified';
}

// Determine priority level
function getPriority(qualification, intentSignals) {
  if (qualification === 'hot' || intentSignals.includes('demo_request')) {
    return 'immediate';
  }
  if (qualification === 'warm' || intentSignals.includes('pricing_interest')) {
    return 'high';
  }
  if (qualification === 'qualified') {
    return 'medium';
  }
  return 'low';
}

// Generate recommended actions
function getRecommendedActions(data, qualification, priority) {
  const actions = [];

  if (priority === 'immediate') {
    actions.push('Schedule immediate sales call');
    actions.push('Send personalized demo invitation');
  }

  if (data.behavior.intent_signals.includes('pricing_interest')) {
    actions.push('Share pricing information');
    actions.push('Offer custom quote');
  }

  if (data.behavior.intent_signals.includes('contact_intent')) {
    actions.push('Initiate contact within 24 hours');
  }

  if (qualification === 'warm' || qualification === 'hot') {
    actions.push('Add to priority nurture campaign');
    actions.push('Research key contacts at company');
  }

  if (data.company.funding_info) {
    actions.push('Review recent funding for expansion opportunities');
  }

  return actions;
}

// Process scoring
const scoringResult = calculateLeadScore(enrichedData);
const qualification = qualifyLead(scoringResult.score);
const priority = getPriority(qualification, enrichedData.behavior.intent_signals);
const recommendedActions = getRecommendedActions(enrichedData, qualification, priority);

// Create final scored lead data
const scoredLead = {
  ...enrichedData,
  lead_score: {
    total_score: scoringResult.score,
    max_score: 100,
    scoring_factors: scoringResult.factors,
    qualification: qualification,
    priority: priority,
    scored_at: new Date().toISOString()
  },
  recommendations: {
    actions: recommendedActions,
    next_steps: getNextSteps(qualification, priority),
    estimated_deal_size: estimateDealSize(enrichedData.company.size, enrichedData.company.industry)
  }
};

function getNextSteps(qualification, priority) {
  if (priority === 'immediate') {
    return 'Contact within 1 hour';
  } else if (priority === 'high') {
    return 'Contact within 4 hours';
  } else if (priority === 'medium') {
    return 'Contact within 24 hours';
  }
  return 'Add to nurture sequence';
}

function estimateDealSize(companySize, industry) {
  let baseSize = 5000; // Base deal size

  // Adjust by company size
  if (companySize === '1000+') baseSize *= 10;
  else if (companySize === '201-1000') baseSize *= 5;
  else if (companySize === '51-200') baseSize *= 2;

  // Adjust by industry
  if (['Finance', 'Healthcare'].includes(industry)) baseSize *= 1.5;
  if (industry === 'Technology') baseSize *= 1.3;

  return `$${baseSize.toLocaleString()} - $${(baseSize * 3).toLocaleString()}`;
}

console.log(`Lead scored: ${scoringResult.score}/100 - ${qualification} (${priority} priority)`);

return {
  json: scoredLead
};

