// Combine all results
const allData = [...hubspotData, ...pipedriveData, ...salesforceData];

// Export data for pandas processing
const csvData = allData.map(record => ({
  source: record.source,
  recordId: record.recordId,
  firstName: record.firstName,
  lastName: record.lastName,
  email: record.email,
  companyName: record.companyName,
  phone: record.phone,
  stage: record.stage,
  lastModified: record.lastModified
}));

// Create CSV content manually (Papa Parse not needed)
const csvHeaders = ['source','recordId','firstName','lastName','email','companyName','phone','stage','lastModified'];
const csvRows = csvData.map(record => 
  csvHeaders.map(header => `"${record[header] || ''}"`).join(',')
);
const csvContent = [csvHeaders.join(','), ...csvRows].join('\n');

// Add metadata and return
const output = {
  runId: config.runId,
  timestamp: new Date().toISOString(),
  totalRecords: allData.length,
  breakdown: {
    hubspot: hubspotData.length,
    pipedrive: pipedriveData.length,
    salesforce: salesforceData.length
  },
  errors: errors,
  data: allData,
  csvData: csvContent,
  csvFilePath: `/tmp/crm_data_${config.runId}.csv`
};

return output;

// Quality Report Generator and Notifier
const config = $('Configuration Center').first().json;
const crmData = $('Parallel CRM Fetcher').first().json;
const processedData = $input.all();

// Calculate statistics
const stats = {
  runId: config.runId,
  timestamp: new Date().toISOString(),
  duration: Math.round((new Date() - new Date(crmData.timestamp)) / 1000),

  // Record counts
  totalFetched: crmData.totalRecords,
  totalProcessed: processedData.length,
  recordsWritten: processedData.filter(r => !r.json.isDuplicate).length,
  duplicatesFound: processedData.filter(r => r.json.isDuplicate).length,

  // Quality metrics
  averageQuality: (processedData.reduce((sum, r) => sum + (r.json.dataQualityScore || 0), 0) / processedData.length).toFixed(3),
  highQuality: processedData.filter(r => r.json.dataQualityScore >= config.qualityThreshold).length,
  lowQuality: processedData.filter(r => r.json.dataQualityScore < config.qualityThreshold).length,

  // Source breakdown
  breakdown: crmData.breakdown,

  // Issues summary
  topIssues: {},
  errors: crmData.errors
};

// Analyze quality issues
processedData.forEach(record => {
  if (record.json.qualityIssues) {
    record.json.qualityIssues.forEach(issue => {
      stats.topIssues[issue] = (stats.topIssues[issue] || 0) + 1;
    });
  }
});

// Sort issues by frequency
stats.topIssues = Object.entries(stats.topIssues)
  .sort((a, b) => b[1] - a[1])
  .slice(0, 5)
  .reduce((obj, [key, value]) => ({ ...obj, [key]: value }), {});

// Create Slack notification
const slackMessage = {
  blocks: [
    {
      type: "header",
      text: {
        type: "plain_text",
        text: "üîÑ CRM Sync Completed"
      }
    },
    {
      type: "section",
      fields: [
        { type: "mrkdwn", text: `*Run ID:* ${stats.runId}` },
        { type: "mrkdwn", text: `*Duration:* ${stats.duration}s` },
        { type: "mrkdwn", text: `*Records Processed:* ${stats.totalProcessed}` },
        { type: "mrkdwn", text: `*Duplicates Found:* ${stats.duplicatesFound}` },
        { type: "mrkdwn", text: `*Average Quality:* ${stats.averageQuality}` },
        { type: "mrkdwn", text: `*High Quality:* ${stats.highQuality}` }
      ]
    }
  ]
};

// Add error section if any
if (stats.errors.length > 0) {
  slackMessage.blocks.push({
    type: "section",
    text: {
      type: "mrkdwn",
      text: `‚ö†Ô∏è *Errors:* ${stats.errors.map(e => `${e.source}: ${e.error}`).join(', ')}`
    }
  });
}

// Send to Slack
if (config.slackWebhookUrl) {
  await $http.post(config.slackWebhookUrl, {
    body: slackMessage,
    headers: { 'Content-Type': 'application/json' }
  });
}

// Write report to Google Sheets
const reportData = {
  ...stats,
  topIssues: JSON.stringify(stats.topIssues),
  errors: JSON.stringify(stats.errors)
};

// Return report for sheet writing
return reportData;

// Comprehensive Error Handler
const error = $input.first().json;
const config = $('Configuration Center').first()?.json || {};

// Create detailed error log
const errorLog = {
  timestamp: new Date().toISOString(),
  runId: config.runId || 'unknown',
  errorType: error.name || 'UnknownError',
  errorMessage: error.message || 'No error message',
  errorStack: error.stack || '',
  nodeName: error.node?.name || 'Unknown node',
  nodeType: error.node?.type || 'Unknown type',
  workflowId: $execution.workflowId,
  executionId: $execution.id,
  context: JSON.stringify(error)
};

// Log to console for debugging
console.error('Workflow Error:', errorLog);

// Send error notification to Slack if configured
if (config.slackWebhookUrl) {
  const slackError = {
    blocks: [
      {
        type: "header",
        text: {
          type: "plain_text",
          text: "‚ùå CRM Sync Error"
        }
      },
      {
        type: "section",
        text: {
          type: "mrkdwn",
          text: `*Error in node:* ${errorLog.nodeName}\n*Message:* ${errorLog.errorMessage}\n*Run ID:* ${errorLog.runId}\n*Time:* ${errorLog.timestamp}`
        }
      }
    ]
  };

  try {
    await $http.post(config.slackWebhookUrl, {
      body: slackError,
      headers: { 'Content-Type': 'application/json' }
    });
  } catch (notifyError) {
    console.error('Failed to send Slack notification:', notifyError);
  }
}

// Write to error log sheet if configured
if (config.masterSheetId) {
  return errorLog;
}

// Return error for further processing
return { error: errorLog, handled: true };
