        ]
      ]
    },
    "Test Webhook Delivery": {
      "main": [
        [
          {
            "node": "Analyze Webhook Response",
            "type": "main",
            "index": 0
          }
        ]
      ]
    }
  }
}

{
  "meta": {
    "instanceId": "api-limits-monitor"
  },
  "nodes": [
    {
      "id": "execute-workflow-trigger",
      "name": "Execute Workflow Trigger",
      "type": "nodes-base.executeWorkflowTrigger",
      "typeVersion": 1,
      "position": [0, 0],
