    },
    {
      "id": "check-rate-limits",
      "name": "Check Rate Limits",
      "type": "nodes-base.httpRequest",
      "typeVersion": 4.2,
      "position": [200, 0],
      "parameters": {
        "url": "={{ $json.endpoint_url }}",
        "method": "GET",
        "sendHeaders": true,
        "headerParameters": {
          "parameters": [
            {
              "name": "Authorization",
              "value": "={{ $json.auth_type === 'bearer' ? 'Bearer ' + $json.auth_token : $json.auth_type === 'apikey' ? $json.auth_token : 'Basic ' + Buffer.from($json.auth_token).toString('base64') }}"
            }
          ]
        },
        "options": {
          "response": {
            "response": {
              "responseFormat": "json",
              "fullResponse": true
            }
          },
          "timeout": 10000
        }
      }
    },
    {
      "id": "analyze-rate-limits",
      "name": "Analyze Rate Limits",
      "type": "nodes-base.code",
      "typeVersion": 2,
      "position": [400, 0],
      "parameters": {
        "language": "javascript",
        "jsCode": "const response = $input.first().json;\nconst headers = response.headers || {};\nconst statusCode = response.statusCode || 0;\n\n// Extract rate limit headers (common variations)\nconst rateLimitUsed = parseInt(headers['x-ratelimit-used'] || headers['x-rate-limit-used'] || headers['ratelimit-used'] || 0);\nconst rateLimitLimit = parseInt(headers['x-ratelimit-limit'] || headers['x-rate-limit-limit'] || headers['ratelimit-limit'] || 0);\nconst rateLimitReset = parseInt(headers['x-ratelimit-reset'] || headers['x-rate-limit-reset'] || headers['ratelimit-reset'] || 0);\nconst rateLimitRemaining = parseInt(headers['x-ratelimit-remaining'] || headers['x-rate-limit-remaining'] || headers['ratelimit-remaining'] || (rateLimitLimit - rateLimitUsed));\n\n// Calculate metrics\nconst usagePercentage = rateLimitLimit > 0 ? Math.round((rateLimitUsed / rateLimitLimit) * 100) : 0;\nconst remainingPercentage = rateLimitLimit > 0 ? Math.round((rateLimitRemaining / rateLimitLimit) * 100) : 100;\n\n// Calculate time to reset\nconst currentTime = Math.floor(Date.now() / 1000);\nconst timeToReset = rateLimitReset > 0 ? rateLimitReset - currentTime : 0;\nconst resetInMinutes = Math.max(0, Math.floor(timeToReset / 60));\n\n// Calculate burn rate (requests per minute)\nconst timeWindow = 3600; // Assume 1 hour window\nconst burnRate = rateLimitLimit > 0 && timeWindow > 0 ? Math.round((rateLimitUsed / timeWindow) * 60) : 0;\n\n// Predict time to exhaustion\nconst requestsRemaining = rateLimitRemaining;\nconst minutesToExhaustion = burnRate > 0 && requestsRemaining > 0 ? Math.floor(requestsRemaining / burnRate) : -1;\n\n// Determine alert status\nlet alertStatus = 'NORMAL';\nif (usagePercentage >= 90) {\n  alertStatus = 'CRITICAL';\n} else if (usagePercentage >= 75) {\n  alertStatus = 'WARNING';\n} else if (usagePercentage >= 50) {\n  alertStatus = 'WATCH';\n}\n\n// Generate recommendations\nconst recommendations = [];\nif (usagePercentage >= 90) {\n  recommendations.push('URGENT: Rate limit nearly exhausted - reduce API calls immediately');\n  recommendations.push('Implement request queuing or throttling');\n} else if (usagePercentage >= 75) {\n  recommendations.push('Rate limit usage is high - monitor closely');\n} else if (usagePercentage >= 50) {\n  recommendations.push('Rate limit usage is moderate - continue monitoring');\n} else {\n  recommendations.push('Rate limit usage is low - operating normally');\n}\n\nif (minutesToExhaustion > 0 && minutesToExhaustion < 60) {\n  recommendations.push(`Rate limit will be exhausted in approximately ${minutesToExhaustion} minutes`);\n}\n\nif (timeToReset > 0) {\n  recommendations.push(`Rate limit resets in ${resetInMinutes} minutes`);\n}\n\nreturn [{\n  tool_name: 'monitor_api_limits',\n  timestamp: new Date().toISOString(),\n  endpoint: $json.endpoint_url,\n  limit_status: alertStatus,\n  rate_limit_metrics: {\n    used: rateLimitUsed,\n    limit: rateLimitLimit,\n    remaining: rateLimitRemaining,\n    usage_percentage: usagePercentage,\n    remaining_percentage: remainingPercentage\n  },\n  timing_analysis: {\n    reset_timestamp: rateLimitReset,\n    time_to_reset_minutes: resetInMinutes,\n    burn_rate_per_minute: burnRate,\n    estimated_exhaustion_minutes: minutesToExhaustion\n  },\n  recommendations: recommendations,\n  requires_immediate_action: usagePercentage >= 90\n}];"
      }
    }
  ],
  "connections": {
    "Execute Workflow Trigger": {
      "main": [
        [
          {
            "node": "Check Rate Limits",
            "type": "main",
            "index": 0
