        [
          {
            "node": "Analyze Rate Limits",
            "type": "main",
            "index": 0
          }
        ]
      ]
    }
  }
}

{
  "meta": {
    "instanceId": "auth-verification-monitor"
  },
  "nodes": [
    {
      "id": "execute-workflow-trigger",
      "name": "Execute Workflow Trigger",
      "type": "nodes-base.executeWorkflowTrigger",
      "typeVersion": 1,
      "position": [0, 0],
      "parameters": {}
    },
    {
      "id": "test-authentication",
      "name": "Test Authentication",
      "type": "nodes-base.httpRequest",
      "typeVersion": 4.2,
      "position": [200, 0],
      "parameters": {
        "url": "={{ $json.endpoint_url }}",
        "method": "GET",
        "sendHeaders": true,
        "headerParameters": {
          "parameters": [
            {
              "name": "Authorization",
              "value": "={{ $json.auth_type === 'bearer' ? 'Bearer ' + $json.auth_token : $json.auth_type === 'apikey' ? $json.auth_token : 'Basic ' + Buffer.from($json.auth_token).toString('base64') }}"
            }
          ]
        },
        "options": {
          "response": {
            "response": {
              "responseFormat": "json",
              "fullResponse": true
            }
          },
          "timeout": 10000,
          "retry": {
            "enabled": false
          }
        }
      }
    },
    {
      "id": "analyze-auth-response",
      "name": "Analyze Auth Response",
      "type": "nodes-base.code",
      "typeVersion": 2,
      "position": [400, 0],
      "parameters": {
        "language": "javascript",
        "jsCode": "const response = $input.first().json;
const statusCode = response.statusCode || 0;
const headers = response.headers || {};
const responseBody = response.body || {};
const responseTime = response.response?.timings?.response || 0;

// Determine authentication status
let authStatus = 'UNKNOWN';
let authValid = false;
let tokenExpired = false;
let insufficientPermissions = false;

if (statusCode === 200 || statusCode === 201) {
  authStatus = 'VALID';
  authValid = true;
} else if (statusCode === 401) {
  authStatus = 'INVALID';
  // Check if it's token expiration
  const errorMessage = JSON.stringify(responseBody).toLowerCase();
  if (errorMessage.includes('expired') || errorMessage.includes('token') && errorMessage.includes('invalid')) {
    tokenExpired = true;
    authStatus = 'EXPIRED';
  }
} else if (statusCode === 403) {
  authStatus = 'INSUFFICIENT_PERMISSIONS';
  insufficientPermissions = true;
} else if (statusCode === 429) {
  authStatus = 'RATE_LIMITED';
} else if (statusCode >= 500) {
  authStatus = 'SERVER_ERROR';
} else {
  authStatus = 'FAILED';
}

// Analyze auth method effectiveness
const authMethod = $json.auth_type || 'unknown';
let methodAnalysis = {
  type: authMethod,
  working: authValid,
  response_time_ms: responseTime
};

// Check for auth-specific headers
const authHeaders = {
  'www-authenticate': headers['www-authenticate'] || null,
  'authorization': headers['authorization'] ? 'present' : 'missing',
  'x-api-key': headers['x-api-key'] ? 'present' : 'missing'
};

// Generate failure patterns analysis
const failurePatterns = [];
if (statusCode === 401) {
  failurePatterns.push('Authentication credentials rejected');
  if (tokenExpired) {
    failurePatterns.push('Token appears to be expired');
  }
}
if (statusCode === 403) {
  failurePatterns.push('Insufficient permissions for this endpoint');
}
if (responseTime > 5000) {
  failurePatterns.push('Slow authentication response time');
}

// Generate recommendations
const recommendations = [];
if (authValid) {
  recommendations.push('Authentication is working correctly');
  if (responseTime > 2000) {
    recommendations.push('Consider monitoring auth response times');
  }
} else {
  if (tokenExpired) {
    recommendations.push('URGENT: Refresh or renew authentication token');
    recommendations.push('Implement automatic token refresh mechanism');
  } else if (insufficientPermissions) {
    recommendations.push('Check API permissions and scopes');
    recommendations.push('Verify account has required access levels');
  } else if (statusCode === 401) {
    recommendations.push('Verify authentication credentials are correct');
    recommendations.push('Check auth method matches API requirements');
  } else {
    recommendations.push('Check API endpoint and authentication method');
    recommendations.push('Verify network connectivity to auth server');
  }
}

return [{
  tool_name: 'verify_authentication',
  timestamp: new Date().toISOString(),
  endpoint: $json.endpoint_url,
  auth_status: authStatus,
  authentication_results: {
    is_valid: authValid,
    status_code: statusCode,
    response_time_ms: responseTime,
    auth_method: authMethod,
    token_expired: tokenExpired,
    insufficient_permissions: insufficientPermissions
  },
  method_analysis: methodAnalysis,
  failure_patterns: failurePatterns,
  auth_headers_detected: authHeaders,
  recommendations: recommendations,
  requires_immediate_action: tokenExpired || (statusCode === 401 && !tokenExpired)
}];"
      }
    }
  ],
  "connections": {
    "Execute Workflow Trigger": {
      "main": [
        [
          {
            "node": "Test Authentication",
            "type": "main",
            "index": 0
          }
        ]
      ]
    },
    "Test Authentication": {
      "main": [
        [
          {
            "node": "Analyze Auth Response",
            "type": "main",
            "index": 0
          }
        ]
      ]
    }
  }
}

{
  "meta": {
    "instanceId": "client-report-generator"
  },
  "nodes": [
    {
      "id": "execute-workflow-trigger",
      "name": "Execute Workflow Trigger",
      "type": "nodes-base.executeWorkflowTrigger",
      "typeVersion": 1,
      "position": [0, 0],
      "parameters": {}
    },
    {
      "id": "aggregate-monitoring-data",
      "name": "Aggregate Monitoring Data",
      "type": "nodes-base.code",
      "typeVersion": 2,
      "position": [200, 0],
      "parameters": {
        "language": "javascript",
        "jsCode": "// Get input data - could be from previous tool executions or provided data
const inputData = $input.first().json;

// Extract individual tool results if they exist
const apiHealthData = inputData.api_health_data || {};
const webhookData = inputData.webhook_data || {};
const limitsData = inputData.limits_data || {};
const authData = inputData.auth_data || {};

// Or use the current input as a single tool result
const toolData = inputData.tool_name ? inputData : {};

// Initialize aggregated metrics
let overallHealth = 'UNKNOWN';
let criticalIssues = [];
let warnings = [];
let recommendations = [];
let healthScore = 0;

// Analyze API Health
if (apiHealthData.health_status || toolData.tool_name === 'analyze_api_health') {
  const healthStatus = apiHealthData.health_status || toolData.health_status;
  if (healthStatus === 'CRITICAL') {
    criticalIssues.push('API endpoint health is critical');
    healthScore += 0;
  } else if (healthStatus === 'WARNING') {
    warnings.push('API endpoint has performance issues');
    healthScore += 50;
  } else if (healthStatus === 'HEALTHY') {
    healthScore += 100;
  }
}

// Analyze Webhook Reliability
if (webhookData.reliability_status || toolData.tool_name === 'validate_webhook_reliability') {
  const webhookStatus = webhookData.reliability_status || toolData.reliability_status;
  if (webhookStatus === 'FAILED') {
    criticalIssues.push('Webhook delivery is failing');
    healthScore += 0;
  } else if (webhookStatus === 'SLOW') {
    warnings.push('Webhook response times are slow');
    healthScore += 50;
  } else if (webhookStatus === 'RELIABLE') {
    healthScore += 100;
  }
}

// Analyze Rate Limits
if (limitsData.limit_status || toolData.tool_name === 'monitor_api_limits') {
  const limitStatus = limitsData.limit_status || toolData.limit_status;
  if (limitStatus === 'CRITICAL') {
    criticalIssues.push('API rate limits nearly exhausted');
    healthScore += 0;
  } else if (limitStatus === 'WARNING') {
    warnings.push('API rate limit usage is high');
    healthScore += 50;
  } else if (limitStatus === 'WATCH') {
    warnings.push('API rate limit usage should be monitored');
    healthScore += 75;
  } else if (limitStatus === 'NORMAL') {
    healthScore += 100;
  }
}

// Analyze Authentication
if (authData.auth_status || toolData.tool_name === 'verify_authentication') {
  const authStatus = authData.auth_status || toolData.auth_status;
  if (authStatus === 'INVALID' || authStatus === 'EXPIRED') {
    criticalIssues.push('Authentication is failing');
    healthScore += 0;
  } else if (authStatus === 'INSUFFICIENT_PERMISSIONS') {
    warnings.push('Authentication has insufficient permissions');
    healthScore += 25;
  } else if (authStatus === 'VALID') {
    healthScore += 100;
  }
}

// Calculate overall health based on number of tools checked
const toolsChecked = Math.max(1, Object.keys({apiHealthData, webhookData, limitsData, authData}).filter(key => 
  eval(key) && Object.keys(eval(key)).length > 0
).length);

const averageScore = healthScore / toolsChecked;

if (criticalIssues.length > 0) {
  overallHealth = 'CRITICAL';
} else if (warnings.length > 0) {
  overallHealth = 'WARNING';
} else if (averageScore >= 80) {
  overallHealth = 'HEALTHY';
} else {
  overallHealth = 'DEGRADED';
}

return [{
  aggregated_data: {
    overall_health: overallHealth,
    health_score: Math.round(averageScore),
    critical_issues: criticalIssues,
    warnings: warnings,
    tools_monitored: toolsChecked,
    api_health: apiHealthData,
    webhook_reliability: webhookData,
    rate_limits: limitsData,
    authentication: authData,
    single_tool_data: toolData
  }
}];"
      }
    },
    {
      "id": "generate-client-report",
      "name": "Generate Client Report",
      "type": "nodes-base.code",
      "typeVersion": 2,
      "position": [400, 0],
      "parameters": {
        "language": "javascript",
        "jsCode": "const data = $input.first().json.aggregated_data;
const timestamp = new Date().toISOString();
const reportDate = new Date().toLocaleDateString();

// Generate Executive Summary
let executiveSummary = '';
if (data.overall_health === 'CRITICAL') {
  executiveSummary = `URGENT: Your API integrations require immediate attention. ${data.critical_issues.length} critical issues detected that may impact service availability.`;
} else if (data.overall_health === 'WARNING') {
  executiveSummary = `Your API integrations are operational but showing signs of degradation. ${data.warnings.length} warnings require monitoring.`;
} else if (data.overall_health === 'HEALTHY') {
  executiveSummary = 'Your API integrations are performing well with no critical issues detected. Continue current monitoring practices.';
} else {
  executiveSummary = 'API integration health assessment completed. Review detailed findings below.';
}

// Generate Performance Metrics Summary
const performanceMetrics = {
  overall_health_score: `${data.health_score}/100`,
  system_status: data.overall_health,
  critical_issues_count: data.critical_issues.length,
  warnings_count: data.warnings.length,
  monitoring_coverage: `${data.tools_monitored} tools active`
};

// Generate Detailed Findings
const detailedFindings = [];

if (data.api_health && Object.keys(data.api_health).length > 0) {
  detailedFindings.push({
    category: 'API Health',
    status: data.api_health.health_status || 'Unknown',
    metrics: data.api_health.metrics || {},
    key_finding: data.api_health.health_status === 'HEALTHY' ? 'API responding normally' : 'API performance issues detected'
  });
}

if (data.webhook_reliability && Object.keys(data.webhook_reliability).length > 0) {
  detailedFindings.push({
    category: 'Webhook Reliability',
    status: data.webhook_reliability.reliability_status || 'Unknown',
    metrics: data.webhook_reliability.delivery_metrics || {},
    key_finding: data.webhook_reliability.reliability_status === 'RELIABLE' ? 'Webhooks delivering successfully' : 'Webhook delivery issues detected'
  });
}

if (data.rate_limits && Object.keys(data.rate_limits).length > 0) {
  detailedFindings.push({
    category: 'Rate Limits',
    status: data.rate_limits.limit_status || 'Unknown',
    metrics: data.rate_limits.rate_limit_metrics || {},
    key_finding: data.rate_limits.limit_status === 'NORMAL' ? 'Rate limit usage is normal' : 'Rate limit concerns detected'
  });
}

if (data.authentication && Object.keys(data.authentication).length > 0) {
  detailedFindings.push({
    category: 'Authentication',
    status: data.authentication.auth_status || 'Unknown',
    metrics: data.authentication.authentication_results || {},
    key_finding: data.authentication.auth_status === 'VALID' ? 'Authentication working correctly' : 'Authentication issues detected'
  });
}

// Generate Prioritized Recommendations
const prioritizedRecommendations = [];

// Critical recommendations first
if (data.critical_issues.length > 0) {
  prioritizedRecommendations.push({
    priority: 'URGENT',
    category: 'Critical Issues',
    actions: data.critical_issues.map(issue => `Address: ${issue}`)
  });
}

// Warning recommendations
if (data.warnings.length > 0) {
  prioritizedRecommendations.push({
    priority: 'HIGH',
    category: 'Performance Optimization',
    actions: data.warnings.map(warning => `Monitor: ${warning}`)
  });
}

// General recommendations
if (data.overall_health === 'HEALTHY') {
  prioritizedRecommendations.push({
    priority: 'MEDIUM',
    category: 'Maintenance',
    actions: [
      'Continue regular monitoring schedule',
      'Review performance trends monthly',
      'Maintain current monitoring configuration'
    ]
  });
}

// Next Steps
const nextSteps = [];
if (data.critical_issues.length > 0) {
  nextSteps.push('1. Address critical issues immediately (within 24 hours)');
  nextSteps.push('2. Implement monitoring alerts for critical thresholds');
  nextSteps.push('3. Schedule follow-up assessment in 48 hours');
} else if (data.warnings.length > 0) {
  nextSteps.push('1. Review warning conditions (within 7 days)');
  nextSteps.push('2. Optimize performance where possible');
  nextSteps.push('3. Schedule follow-up assessment in 2 weeks');
} else {
  nextSteps.push('1. Continue current monitoring schedule');
  nextSteps.push('2. Review monthly performance trends');
  nextSteps.push('3. Next comprehensive assessment in 30 days');
}

return [{
  tool_name: 'generate_client_report',
  timestamp: timestamp,
