// Simplified CRE Analysis & Dashboard
const scrapedData = $input.all()[0].json;
const listings = scrapedData.listings || [];

// Core analysis
const analysis = {
  total_listings: listings.length,
  avg_lease_rate: 0,
  property_types: {},
  top_locations: {},
  opportunities: [],
  alerts: []
};

// Process listings
let totalRate = 0;
let rateCount = 0;

listings.forEach(listing => {
  // Track property types
  const type = listing.type || 'Unknown';
  analysis.property_types[type] = (analysis.property_types[type] || 0) + 1;

  // Track locations
  const location = listing.location || 'Unknown';
  analysis.top_locations[location] = (analysis.top_locations[location] || 0) + 1;

  // Calculate average rates
  if (listing.lease_rate) {
    totalRate += listing.lease_rate;
    rateCount++;
  }

  // Identify opportunities (below market rate)
  if (listing.lease_rate && listing.lease_rate < 20) {
    analysis.opportunities.push({
      id: listing.id,
      type: listing.type,
      location: listing.location,
      rate: listing.lease_rate,
      size: listing.size_sqft,
      opportunity: 'Below Market Rate'
    });
  }
});

analysis.avg_lease_rate = rateCount > 0 ? (totalRate / rateCount).toFixed(2) : 0;

// Generate alerts
if (analysis.opportunities.length > 10) {
  analysis.alerts.push({
    type: 'High Opportunity Count',
    message: `${analysis.opportunities.length} below-market properties found`,
    priority: 'High'
  });
}

if (analysis.avg_lease_rate > 30) {
  analysis.alerts.push({
    type: 'Rate Alert',
    message: `Average lease rate (${analysis.avg_lease_rate}) above $30/sqft`,
    priority: 'Medium'
  });
}

// Create dashboard summary
const dashboard = {
  generated_at: new Date().toISOString(),
  market_summary: {
    total_properties: analysis.total_listings,
    avg_lease_rate: `${analysis.avg_lease_rate}/sqft`,
    opportunities_found: analysis.opportunities.length,
    active_alerts: analysis.alerts.length
  },
  top_property_types: Object.entries(analysis.property_types)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 5)
    .map(([type, count]) => ({ type, count })),
  top_locations: Object.entries(analysis.top_locations)
    .sort(([,a], [,b]) => b - a)
    .slice(0, 5)
    .map(([location, count]) => ({ location, count })),
  priority_opportunities: analysis.opportunities
    .sort((a, b) => a.rate - b.rate)
    .slice(0, 10),
  alerts: analysis.alerts,
  next_actions: [
    analysis.opportunities.length > 0 ? `Review ${analysis.opportunities.length} opportunities` : 'No immediate opportunities',
    analysis.alerts.length > 0 ? `Address ${analysis.alerts.length} alerts` : 'No alerts requiring action',
    'Update market analysis tomorrow'
  ]
};

console.log(`CRE Analysis Complete: ${dashboard.market_summary.total_properties} properties, ${dashboard.market_summary.opportunities_found} opportunities`);

return [{ json: dashboard }];
