// Process incoming data from either email or webhook
const inputData = $input.all()[0];
let invoiceFiles = [];

// Handle email attachments
if (inputData.json.attachments) {
  invoiceFiles = inputData.json.attachments
    .filter(attachment => {
      const fileName = attachment.filename.toLowerCase();
      return fileName.endsWith('.pdf') || 
             fileName.endsWith('.png') || 
             fileName.endsWith('.jpg') || 
             fileName.endsWith('.jpeg') ||
             fileName.endsWith('.tiff') ||
             fileName.endsWith('.bmp');
    })
    .map(attachment => ({
      filename: attachment.filename,
      content: attachment.content,
      contentType: attachment.contentType,
      source: 'email',
      sender: inputData.json.from?.text || 'unknown',
      subject: inputData.json.subject || 'No subject',
      receivedDate: inputData.json.date || new Date().toISOString()
    }));
}
// Handle direct file uploads
else if (inputData.binary) {
  Object.keys(inputData.binary).forEach(key => {
    const file = inputData.binary[key];
    invoiceFiles.push({
      filename: file.fileName || `upload_${key}`,
      content: file.data,
      contentType: file.mimeType,
      source: 'upload',
      uploadedDate: new Date().toISOString()
    });
  });
}

// Return each file as separate execution
return invoiceFiles.map((file, index) => ({
  json: {
    invoice_id: `INV_${Date.now()}_${index}`,
    filename: file.filename,
    source: file.source,
    sender: file.sender || null,
    subject: file.subject || null,
    received_date: file.receivedDate || file.uploadedDate,
    content_type: file.contentType,
    processing_status: 'pending',
    created_at: new Date().toISOString()
  },
  binary: {
    invoice_file: {
      data: file.content,
      mimeType: file.contentType,
      fileName: file.filename
    }
  }
}));

// Enhanced data extraction and cleaning
const inputData = $input.all()[0];
const extractedData = inputData.json.result?.invoice_data || {};
const originalMetadata = inputData.json;

function cleanAndValidateData(data) {
  // Clean and standardize extracted data
  const cleaned = {
    // Basic invoice info
    invoice_number: cleanString(data.invoice_number),
    invoice_date: standardizeDate(data.invoice_date),
    due_date: standardizeDate(data.due_date),

    // Vendor information
    vendor: {
      name: cleanString(data.vendor_name),
      address: cleanString(data.vendor_address),
      tax_id: cleanString(data.vendor_tax_id || data.vendor_vat || data.tax_id),
      email: cleanEmail(data.vendor_email),
      phone: cleanPhone(data.vendor_phone)
    },

    // Bill to information
    bill_to: {
      name: cleanString(data.bill_to_name),
      address: cleanString(data.bill_to_address)
    },

    // Financial data
    currency: (data.currency || 'USD').toUpperCase(),
    amounts: {
      subtotal: parseAmount(data.subtotal),
      tax_total: parseAmount(data.tax_total),
      discount_amount: parseAmount(data.discount_amount || '0'),
      shipping_amount: parseAmount(data.shipping_amount || '0'),
      total_amount: parseAmount(data.total_amount)
    },

    // Line items
    line_items: cleanLineItems(data.line_items || []),

    // Additional info
    payment_terms: cleanString(data.payment_terms),
    purchase_order: cleanString(data.purchase_order),
    notes: cleanString(data.notes),

    // Processing metadata
    processing_info: {
      extracted_at: new Date().toISOString(),
      confidence_score: calculateConfidenceScore(data),
      data_completeness: calculateCompleteness(data)
    }
  };

  return cleaned;
}

function cleanString(str) {
  if (!str || str === 'null' || str === 'undefined') return null;
  return str.toString().trim().replace(/\s+/g, ' ');
}

function standardizeDate(dateStr) {
  if (!dateStr || dateStr === 'null') return null;
  try {
    const date = new Date(dateStr);
    return date.toISOString().split('T')[0]; // Return YYYY-MM-DD format
  } catch {
    return dateStr; // Return original if parsing fails
  }
}

function cleanEmail(email) {
  if (!email || email === 'null') return null;
  const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
  return emailRegex.test(email) ? email.toLowerCase() : null;
}

function cleanPhone(phone) {
  if (!phone || phone === 'null') return null;
  return phone.replace(/[^\d+\-\(\)\s]/g, '').trim();
}

function parseAmount(amount) {
  if (!amount || amount === 'null') return 0;
  const cleaned = amount.toString().replace(/[^\d.-]/g, '');
  return parseFloat(cleaned) || 0;
}

function cleanLineItems(items) {
  if (!Array.isArray(items)) return [];

  return items.map(item => ({
    description: cleanString(item.description),
    quantity: parseFloat(item.quantity) || 1,
    unit_price: parseAmount(item.unit_price),
    total: parseAmount(item.total),
    tax_rate: cleanString(item.tax_rate),
    tax_amount: parseAmount(item.tax_amount || '0')
  })).filter(item => item.description); // Remove items without description
}

function calculateConfidenceScore(data) {
  let score = 0;
  const requiredFields = ['invoice_number', 'vendor_name', 'total_amount', 'invoice_date'];

  requiredFields.forEach(field => {
    if (data[field] && data[field] !== 'null') score += 25;
  });

  return score;
}

function calculateCompleteness(data) {
  const allFields = [
    'invoice_number', 'invoice_date', 'due_date', 'vendor_name', 
    'vendor_address', 'total_amount', 'subtotal', 'line_items'
  ];

  const completedFields = allFields.filter(field => 
    data[field] && data[field] !== 'null' && 
    (Array.isArray(data[field]) ? data[field].length > 0 : true)
  ).length;

  return Math.round((completedFields / allFields.length) * 100);
}

const cleanedData = cleanAndValidateData(extractedData);

return [{
  json: {
    ...originalMetadata,
    extracted_invoice_data: cleanedData,
    processing_status: 'extracted',
    updated_at: new Date().toISOString()
  },
  binary: inputData.binary // Preserve binary data for potential reprocessing
}];

// Comprehensive validation rules for invoice data
const inputData = $input.all()[0];
const invoiceData = inputData.json.extracted_invoice_data;
const validationResults = {
  is_valid: true,
  errors: [],
  warnings: [],
  validation_score: 0,
  business_rules: {
    duplicate_check: 'pending',
    amount_threshold: 'pending',
    vendor_verification: 'pending',
    date_validation: 'pending'
  }
};

// Required field validation
const requiredFields = [
  { field: 'invoice_number', message: 'Invoice number is required' },
  { field: 'vendor.name', message: 'Vendor name is required' },
  { field: 'amounts.total_amount', message: 'Total amount is required' },
  { field: 'invoice_date', message: 'Invoice date is required' }
];

requiredFields.forEach(({ field, message }) => {
  const value = getNestedValue(invoiceData, field);
  if (!value || value === 0) {
    validationResults.errors.push(message);
    validationResults.is_valid = false;
  } else {
    validationResults.validation_score += 20;
  }
});

// Date validation
if (invoiceData.invoice_date && invoiceData.due_date) {
  const invoiceDate = new Date(invoiceData.invoice_date);
  const dueDate = new Date(invoiceData.due_date);

  if (dueDate < invoiceDate) {
    validationResults.errors.push('Due date cannot be before invoice date');
    validationResults.is_valid = false;
  } else {
    validationResults.business_rules.date_validation = 'passed';
  }
} else {
  validationResults.business_rules.date_validation = 'failed';
}

// Amount validation
const amounts = invoiceData.amounts;
if (amounts) {
  // Check if total matches calculation
  const calculatedTotal = amounts.subtotal + amounts.tax_total + amounts.shipping_amount - amounts.discount_amount;
  const totalDifference = Math.abs(calculatedTotal - amounts.total_amount);

  if (totalDifference > 0.01) {
    validationResults.warnings.push(`Total amount mismatch: Expected ${calculatedTotal.toFixed(2)}, got ${amounts.total_amount.toFixed(2)}`);
  }

  // Amount threshold check (configurable)
  const amountThreshold = 10000; // $10,000
  if (amounts.total_amount > amountThreshold) {
    validationResults.business_rules.amount_threshold = 'requires_approval';
    validationResults.warnings.push(`High amount invoice requires additional approval: $${amounts.total_amount}`);
  } else {
    validationResults.business_rules.amount_threshold = 'passed';
  }
} else {
  validationResults.errors.push('Amount information is missing');
  validationResults.is_valid = false;
}

// Vendor validation
if (invoiceData.vendor && invoiceData.vendor.name) {
  // Simulate vendor verification (in real scenario, check against approved vendor list)
  const approvedVendors = [
    'ABC Company Ltd', 'XYZ Corporation', 'Tech Solutions Inc', 
    'Office Supplies Co', 'Consulting Partners LLC'
  ];

  const isApprovedVendor = approvedVendors.some(vendor => 
    vendor.toLowerCase().includes(invoiceData.vendor.name.toLowerCase()) ||
    invoiceData.vendor.name.toLowerCase().includes(vendor.toLowerCase())
  );

  if (isApprovedVendor) {
    validationResults.business_rules.vendor_verification = 'approved';
    validationResults.validation_score += 15;
  } else {
    validationResults.business_rules.vendor_verification = 'new_vendor';
    validationResults.warnings.push('Vendor not in approved list - requires verification');
  }
}

// Line items validation
if (invoiceData.line_items && invoiceData.line_items.length > 0) {
  let lineItemErrors = [];

  invoiceData.line_items.forEach((item, index) => {
    if (!item.description) {
      lineItemErrors.push(`Line item ${index + 1}: Missing description`);
    }
    if (item.quantity <= 0) {
      lineItemErrors.push(`Line item ${index + 1}: Invalid quantity`);
    }
    if (item.unit_price <= 0) {
      lineItemErrors.push(`Line item ${index + 1}: Invalid unit price`);
    }

    // Check line total calculation
    const expectedTotal = item.quantity * item.unit_price;
    if (Math.abs(expectedTotal - item.total) > 0.01) {
      lineItemErrors.push(`Line item ${index + 1}: Total calculation error`);
    }
  });

  if (lineItemErrors.length > 0) {
    validationResults.warnings.push(...lineItemErrors);
  } else {
    validationResults.validation_score += 10;
  }
} else {
  validationResults.warnings.push('No line items found');
}

// Duplicate invoice check (simulate database lookup)
if (invoiceData.invoice_number) {
  // In real scenario, check against database
  const existingInvoices = ['INV-001', 'INV-002', 'INV-003']; // Mock data

  if (existingInvoices.includes(invoiceData.invoice_number)) {
    validationResults.errors.push('Duplicate invoice number detected');
    validationResults.business_rules.duplicate_check = 'duplicate';
    validationResults.is_valid = false;
  } else {
    validationResults.business_rules.duplicate_check = 'unique';
    validationResults.validation_score += 15;
  }
}

// Calculate confidence level
let confidenceLevel = 'low';
if (validationResults.validation_score >= 80) confidenceLevel = 'high';
else if (validationResults.validation_score >= 60) confidenceLevel = 'medium';

// Determine approval requirements
let requiresApproval = false;
let approvalReason = [];

if (validationResults.business_rules.amount_threshold === 'requires_approval') {
  requiresApproval = true;
  approvalReason.push('High amount');
}

if (validationResults.business_rules.vendor_verification === 'new_vendor') {
  requiresApproval = true;
  approvalReason.push('New vendor');
}

if (validationResults.errors.length > 0) {
  requiresApproval = true;
  approvalReason.push('Validation errors');
}

function getNestedValue(obj, path) {
  return path.split('.').reduce((current, key) => current?.[key], obj);
}

return [{
  json: {
    ...inputData.json,
    validation_results: validationResults,
    confidence_level: confidenceLevel,
    requires_approval: requiresApproval,
    approval_reasons: approvalReason,
    processing_status: validationResults.is_valid ? 'validated' : 'validation_failed',
    validated_at: new Date().toISOString()
  },
  binary: inputData.binary // Preserve binary data
}];

// Generate approval request with comprehensive details
const inputData = $input.all()[0];
const invoiceData = inputData.json.extracted_invoice_data;
const validation = inputData.json.validation_results;

// Create approval request
const approvalRequest = {
  approval_id: `APPR_${inputData.json.invoice_id}_${Date.now()}`,
  invoice_id: inputData.json.invoice_id,
  request_type: 'invoice_approval',
  priority: determinePriority(inputData.json.approval_reasons),

  // Invoice summary
  invoice_summary: {
    invoice_number: invoiceData.invoice_number,
    vendor_name: invoiceData.vendor ? invoiceData.vendor.name : 'Unknown',
    total_amount: invoiceData.amounts ? invoiceData.amounts.total_amount : 0,
    currency: invoiceData.currency,
    invoice_date: invoiceData.invoice_date,
    due_date: invoiceData.due_date
  },

  // Approval details
  approval_reasons: inputData.json.approval_reasons,
  validation_score: validation.validation_score,
  confidence_level: inputData.json.confidence_level,

  // Issues requiring attention
  attention_required: {
    errors: validation.errors,
    warnings: validation.warnings,
    business_rule_flags: Object.entries(validation.business_rules)
      .filter(([key, value]) => value === 'requires_approval' || value === 'failed')
      .map(([key, value]) => ({ rule: key, status: value }))
  },

  // Approval workflow
  workflow_steps: generateApprovalSteps(inputData.json.approval_reasons, invoiceData.amounts ? invoiceData.amounts.total_amount : 0),

  // Request metadata
  requested_at: new Date().toISOString(),
  requested_by: 'system',
  status: 'pending_approval',

  // Generate approval message
  approval_message: generateApprovalMessage(invoiceData, validation, inputData.json.approval_reasons)
};

function determinePriority(reasons) {
  if (reasons.includes('Validation errors')) return 'high';
  if (reasons.includes('High amount')) return 'medium';
  return 'normal';
}

function generateApprovalSteps(reasons, amount) {
  const steps = [];

  // Finance team review for high amounts
  if (amount > 10000) {
    steps.push({
      step: 1,
      approver_role: 'finance_manager',
      required: true,
      description: 'Finance manager approval for high-value invoice'
    });
  }

  // Procurement review for new vendors
  if (reasons.includes('New vendor')) {
    steps.push({
      step: steps.length + 1,
      approver_role: 'procurement_manager',
      required: true,
      description: 'Procurement verification for new vendor'
    });
  }

  // Department head approval
  steps.push({
    step: steps.length + 1,
    approver_role: 'department_head',
    required: true,
    description: 'Department head final approval'
  });

  return steps;
}

function generateApprovalMessage(invoiceData, validation, reasons) {
  let message = `üßæ **INVOICE APPROVAL REQUEST**\n\n`;
  message += `üìã **Invoice:** ${invoiceData.invoice_number || 'N/A'}\n`;
  message += `üè¢ **Vendor:** ${invoiceData.vendor ? invoiceData.vendor.name : 'Unknown'}\n`;

  const amount = invoiceData.amounts ? invoiceData.amounts.total_amount : 0;
  const currency = invoiceData.currency || 'USD';
  message += `üí∞ **Amount:** ${currency} ${amount.toLocaleString()}\n`;
  message += `üìÖ **Date:** ${invoiceData.invoice_date || 'N/A'}\n`;
  message += `‚è∞ **Due:** ${invoiceData.due_date || 'N/A'}\n\n`;

  message += `üö® **Approval Required For:**\n`;
  reasons.forEach(reason => {
    message += `‚Ä¢ ${reason}\n`;
  });

  if (validation.errors.length > 0) {
    message += `\n‚ùå **Errors:**\n`;
    validation.errors.forEach(error => {
      message += `‚Ä¢ ${error}\n`;
    });
  }

  if (validation.warnings.length > 0) {
    message += `\n‚ö†Ô∏è **Warnings:**\n`;
    validation.warnings.forEach(warning => {
      message += `‚Ä¢ ${warning}\n`;
    });
  }

  message += `\nüìä **Validation Score:** ${validation.validation_score}/100\n`;
  message += `üéØ **Confidence:** ${inputData.json.confidence_level.toUpperCase()}\n\n`;
  message += `Please review and approve/reject this invoice.`;

  return message;
}

return [{
  json: {
    ...inputData.json,
    approval_request: approvalRequest,
    processing_status: 'pending_approval',
    updated_at: new Date().toISOString()
  },
  binary: inputData.binary // Preserve binary data
}];
