// Simple file processor
const inputData = $input.all()[0];
const files = [];

// Handle file uploads
if (inputData.binary) {
  Object.keys(inputData.binary).forEach(key => {
    const file = inputData.binary[key];
    files.push({
      filename: file.fileName,
      contentType: file.mimeType,
      uploadedAt: new Date().toISOString()
    });
  });
}

return files.map(file => ({
  json: {
    filename: file.filename,
    contentType: file.contentType,
    uploadedAt: file.uploadedAt,
    status: 'ready_for_processing'
  },
  binary: inputData.binary
}));

// Clean and validate extracted data
const inputData = $input.all()[0];
let extractedData = {};

// Parse AI response
try {
  const content = inputData.json.message?.content || inputData.json;
  if (typeof content === 'string') {
    const jsonMatch = content.match(/{[\s\S]*}/);
    if (jsonMatch) {
      extractedData = JSON.parse(jsonMatch[0]);
    }
  } else {
    extractedData = content;
  }
} catch (error) {
  console.log('Parse error:', error.message);
  extractedData = { transactions: [] };
}

// Clean transaction data
const cleanTransactions = (extractedData.transactions || []).map((tx, index) => ({
  id: `tx_${Date.now()}_${index}`,
  date: tx.date,
  description: (tx.description || '').trim().toUpperCase(),
  amount: parseFloat(tx.amount) || 0,
  category: tx.category || 'other',
  processed_at: new Date().toISOString()
}));

// Calculate summary
const totalExpenses = cleanTransactions
  .filter(tx => tx.amount < 0)
  .reduce((sum, tx) => sum + Math.abs(tx.amount), 0);

const totalIncome = cleanTransactions
  .filter(tx => tx.amount > 0)
  .reduce((sum, tx) => sum + tx.amount, 0);

const categoryTotals = {};
cleanTransactions.forEach(tx => {
  if (tx.amount < 0) { // Only expenses
    categoryTotals[tx.category] = (categoryTotals[tx.category] || 0) + Math.abs(tx.amount);
  }
});

return [{
  json: {
    account_info: {
      account_number: extractedData.account_number || 'Unknown',
      bank_name: extractedData.bank_name || 'Unknown',
      statement_period: extractedData.statement_period || 'Unknown',
      opening_balance: parseFloat(extractedData.opening_balance) || 0,
      closing_balance: parseFloat(extractedData.closing_balance) || 0
    },
    transactions: cleanTransactions,
    summary: {
      total_transactions: cleanTransactions.length,
      total_expenses: totalExpenses,
      total_income: totalIncome,
      net_change: totalIncome - totalExpenses,
      category_breakdown: categoryTotals
    },
    processed_at: new Date().toISOString(),
    status: 'completed'
  }
}]);
