const output = [];

for (const item of $input.all()) {
  // 1. Grab the raw string from the 'output' field
  let raw = item.json.output;
  if (typeof raw !== 'string') {
    // nothing to do
    output.push({ json: {} });
    continue;
  }

  // 2. Remove `````` fences
  raw = raw.replace(/``````/g, '').trim();

  // 3. Extract the JSON payload between the first { and last }
  const start = raw.indexOf('{');
  const end = raw.lastIndexOf('}');
  const jsonString = (start !== -1 && end !== -1)
    ? raw.substring(start, end + 1)
    : raw;

  // 4. Attempt to parse
  try {
    const parsed = JSON.parse(jsonString);
    output.push({ json: parsed });
  } catch (e) {
    // on error, output the raw string and error
    output.push({
      json: {
        error: e.message,
        raw: jsonString
      }
    });
  }
}

return output;

