// Process stock additions or deductions
const items = $input.all();
const updatedStock = [];

// Example: Assume input contains stock updates (e.g., from a form or API)
const stockUpdates = [
  { item: 'Cement', action: 'add', quantity: 50 },
  { item: 'Paint', action: 'use', quantity: 10 }
  // Replace with dynamic input from a form or API
];

for (const item of items) {
  let stock = item.json;
  const update = stockUpdates.find(u => u.item === stock.item);
  if (update) {
    if (update.action === 'add') {
      stock.quantity += update.quantity;
    } else if (update.action === 'use') {
      stock.quantity = Math.max(0, stock.quantity - update.quantity);
    }
    stock.last_updated = new Date().toISOString();
  }
  updatedStock.push(stock);
}

return updatedStock.map(s => ({ json: s }));

// Check for low stock levels
const items = $input.all();
const lowStockItems = [];

for (const item of items) {
  const stock = item.json;
  // Assume threshold is defined in Google Sheet or set here (e.g., 20)
  const threshold = stock.threshold || 20;
  if (stock.quantity <= threshold) {
    lowStockItems.push({
      item: stock.item,
      quantity: stock.quantity,
      threshold: threshold,
      alert_message: `Low stock alert: ${stock.item} has ${stock.quantity} units remaining (threshold: ${threshold})`
    });
  }
}

return lowStockItems.map(item => ({ json: item }));
