// Get the analyzed asset data
const assetData = $input.all()[0].json.result;

// Enhanced tag generation based on analysis
function generateTags(data) {
  const tags = new Set();

  // Add basic type tags
  if (data.asset_type) {
    tags.add(data.asset_type);
    tags.add(`${data.asset_type}-content`);
  }

  // Add color-based tags
  if (data.primary_colors && data.primary_colors.length > 0) {
    data.primary_colors.forEach(color => {
      const colorName = getColorName(color);
      if (colorName) tags.add(colorName);
    });
  }

  // Add style tags
  if (data.style_elements) {
    data.style_elements.forEach(style => tags.add(style));
  }

  // Add suggested tags
  if (data.suggested_tags) {
    data.suggested_tags.forEach(tag => tags.add(tag));
  }

  // Add usage context tags
  if (data.usage_context) {
    tags.add(data.usage_context);
    tags.add(`${data.usage_context}-ready`);
  }

  // Add brand element tags
  if (data.brand_elements) {
    data.brand_elements.forEach(element => tags.add(`brand-${element}`));
  }

  // Add dimension-based tags
  if (data.dimensions) {
    const [width, height] = data.dimensions.split('x').map(Number);
    if (width > height) tags.add('landscape');
    else if (height > width) tags.add('portrait');
    else tags.add('square');

    // Add size categories
    if (width >= 1920) tags.add('high-resolution');
    if (width <= 800) tags.add('web-optimized');
  }

  return Array.from(tags);
}

// Simple color name mapping
function getColorName(hex) {
  const colorMap = {
    '#FF0000': 'red', '#00FF00': 'green', '#0000FF': 'blue',
    '#FFFF00': 'yellow', '#FF00FF': 'magenta', '#00FFFF': 'cyan',
    '#000000': 'black', '#FFFFFF': 'white', '#808080': 'gray'
  };
  return colorMap[hex.toUpperCase()];
}

const generatedTags = generateTags(assetData);

return [{
  json: {
    ...assetData,
    generated_tags: generatedTags,
    tag_count: generatedTags.length,
    processed_at: new Date().toISOString()
  }
}];

// Brand compliance checker
const assetData = $input.all()[0].json;

// Define brand guidelines (customize these for your brand)
const brandGuidelines = {
  approvedColors: ['#FF6B35', '#004E89', '#1A659E', '#FFFFFF', '#000000'],
  forbiddenColors: ['#FF0000', '#00FF00'], // Bright red and green
  requiredElements: ['brand-logo'],
  approvedFonts: ['Arial', 'Helvetica', 'Roboto'],
  maxFileSize: 10485760, // 10MB in bytes
  approvedFormats: ['jpg', 'png', 'svg', 'pdf', 'mp4'],
  minResolution: { width: 800, height: 600 }
};

function checkBrandCompliance(data, guidelines) {
  const compliance = {
    score: 100,
    issues: [],
    warnings: [],
    passed: true,
    details: {}
  };

  // Check colors
  if (data.primary_colors) {
    const nonCompliantColors = data.primary_colors.filter(
      color => !guidelines.approvedColors.includes(color.toUpperCase())
    );

    if (nonCompliantColors.length > 0) {
      compliance.issues.push(`Non-brand colors detected: ${nonCompliantColors.join(', ')}`);
      compliance.score -= 20;
    }

    // Check for forbidden colors
    const forbiddenFound = data.primary_colors.filter(
      color => guidelines.forbiddenColors.includes(color.toUpperCase())
    );

    if (forbiddenFound.length > 0) {
      compliance.issues.push(`Forbidden colors used: ${forbiddenFound.join(', ')}`);
      compliance.score -= 30;
    }
  }

  // Check brand elements
  if (data.brand_elements) {
    const missingElements = guidelines.requiredElements.filter(
      element => !data.brand_elements.includes(element)
    );

    if (missingElements.length > 0) {
      compliance.warnings.push(`Missing brand elements: ${missingElements.join(', ')}`);
      compliance.score -= 10;
    }
  } else {
    compliance.issues.push('No brand elements detected');
    compliance.score -= 25;
  }

  // Check file format
  if (data.file_format && !guidelines.approvedFormats.includes(data.file_format.toLowerCase())) {
    compliance.issues.push(`Unsupported file format: ${data.file_format}`);
    compliance.score -= 15;
  }

  // Check dimensions
  if (data.dimensions) {
    const [width, height] = data.dimensions.split('x').map(Number);
    if (width < guidelines.minResolution.width || height < guidelines.minResolution.height) {
      compliance.warnings.push(`Resolution below recommended minimum (${guidelines.minResolution.width}x${guidelines.minResolution.height})`);
      compliance.score -= 5;
    }
  }

  // Determine overall compliance status
  if (compliance.score >= 90) {
    compliance.status = 'approved';
  } else if (compliance.score >= 70) {
    compliance.status = 'approved-with-warnings';
  } else {
    compliance.status = 'rejected';
    compliance.passed = false;
  }

  return compliance;
}

const complianceResult = checkBrandCompliance(assetData, brandGuidelines);

return [{
  json: {
    ...assetData,
    brand_compliance: complianceResult,
    compliance_checked_at: new Date().toISOString()
  }
}];

// Asset organizer - creates folder structure and metadata
const assetData = $input.all()[0].json;

function organizeAsset(data) {
  const organization = {
    folder_structure: [],
    metadata: {},
    file_naming: '',
    storage_location: ''
  };

  // Create folder structure based on asset properties
  const basePath = '/creative-assets';

  // Year/Month organization
  const now = new Date();
  const year = now.getFullYear();
  const month = String(now.getMonth() + 1).padStart(2, '0');

  organization.folder_structure.push(basePath);
  organization.folder_structure.push(`${basePath}/${year}`);
  organization.folder_structure.push(`${basePath}/${year}/${month}`);

  // Asset type folder
  if (data.asset_type) {
    organization.folder_structure.push(`${basePath}/${year}/${month}/${data.asset_type}`);
  }

  // Usage context subfolder
  if (data.usage_context) {
    const contextPath = `${basePath}/${year}/${month}/${data.asset_type}/${data.usage_context}`;
    organization.folder_structure.push(contextPath);
    organization.storage_location = contextPath;
  }

  // Generate standardized filename
  const timestamp = now.toISOString().replace(/[:.]/g, '-').slice(0, 19);
  const assetType = data.asset_type || 'asset';
  const context = data.usage_context || 'general';
  const extension = data.file_format || 'unknown';

  organization.file_naming = `${assetType}-${context}-${timestamp}.${extension}`;

  // Create comprehensive metadata
  organization.metadata = {
    original_filename: data.original_filename || 'unknown',
    upload_date: now.toISOString(),
    asset_type: data.asset_type,
    dimensions: data.dimensions,
    file_format: data.file_format,
    primary_colors: data.primary_colors,
    tags: data.generated_tags,
    brand_compliance: data.brand_compliance,
    usage_context: data.usage_context,
    style_elements: data.style_elements,
    content_description: data.content_description,
    text_content: data.text_content,
    searchable_keywords: [
      ...data.generated_tags || [],
      ...data.style_elements || [],
      data.asset_type,
      data.usage_context
    ].filter(Boolean).join(' ').toLowerCase()
  };

  return organization;
}

const organizationResult = organizeAsset(assetData);

return [{
  json: {
    ...assetData,
    organization: organizationResult,
    organized_at: new Date().toISOString(),
    // Create final asset record
    asset_record: {
      id: `asset_${Date.now()}_${Math.random().toString(36).substr(2, 9)}`,
      name: organizationResult.file_naming,
      path: organizationResult.storage_location,
      metadata: organizationResult.metadata,
      status: assetData.brand_compliance?.status || 'pending',
      created_at: new Date().toISOString()
    }
  }
}];
