// Get the input data from ScrapeGraphAI
const inputData = $input.all()[0].json;

// Extract potential infringements from result
const potentialInfringements = inputData.result.copyright_matches || inputData.result.matches || inputData.result.infringements || [];

// Define your copyrighted content for comparison
const copyrightedContent = {
  texts: [
    "Your unique copyrighted text here",
    "Another protected phrase or paragraph",
    "Brand slogan or tagline"
  ],
  brandNames: ["YourBrand", "CompanyName", "ProductName"],
  domains: ["yourwebsite.com", "yourcompany.com"]
};

// Function to calculate similarity score
function calculateSimilarity(text1, text2) {
  const words1 = text1.toLowerCase().split(/\W+/);
  const words2 = text2.toLowerCase().split(/\W+/);
  const intersection = words1.filter(word => words2.includes(word));
  return intersection.length / Math.max(words1.length, words2.length);
}

// Function to analyze potential infringement
function analyzeInfringement(item) {
  const analysis = {
    url: item.url,
    title: item.title,
    content_snippet: item.content_snippet,
    domain: item.domain,
    date_found: new Date().toISOString().split('T')[0],
    infringement_type: [],
    similarity_scores: [],
    risk_level: 'low',
    requires_action: false
  };

  // Check for exact text matches
  copyrightedContent.texts.forEach((protectedText, index) => {
    const similarity = calculateSimilarity(protectedText, item.content_snippet || '');
    analysis.similarity_scores.push({
      text_index: index,
      score: similarity
    });

    if (similarity > 0.8) {
      analysis.infringement_type.push('high_similarity_text');
      analysis.risk_level = 'high';
      analysis.requires_action = true;
    } else if (similarity > 0.5) {
      analysis.infringement_type.push('moderate_similarity_text');
      analysis.risk_level = analysis.risk_level === 'low' ? 'medium' : analysis.risk_level;
    }
  });

  // Check for brand name usage
  const contentLower = (item.content_snippet || '').toLowerCase();
  const titleLower = (item.title || '').toLowerCase();

  copyrightedContent.brandNames.forEach(brand => {
    if (contentLower.includes(brand.toLowerCase()) || titleLower.includes(brand.toLowerCase())) {
      analysis.infringement_type.push('unauthorized_brand_usage');
      analysis.risk_level = analysis.risk_level === 'low' ? 'medium' : 'high';
      if (analysis.risk_level === 'high') {
        analysis.requires_action = true;
      }
    }
  });

  // Check if it's from competitor or suspicious domain
  const suspiciousDomains = ['competitor.com', 'copycatsite.com'];
  if (suspiciousDomains.some(domain => item.url?.includes(domain))) {
    analysis.infringement_type.push('competitor_usage');
    analysis.risk_level = 'high';
    analysis.requires_action = true;
  }

  // Filter out our own domains
  if (copyrightedContent.domains.some(domain => item.url?.includes(domain))) {
    return null; // Skip our own content
  }

  return analysis;
}

// Process all potential infringements
const analyzedResults = potentialInfringements
  .map(analyzeInfringement)
  .filter(result => result !== null)
  .filter(result => result.requires_action || result.risk_level !== 'low');

console.log(`Analyzed ${potentialInfringements.length} potential matches, found ${analyzedResults.length} concerning cases`);

// Return each concerning case as separate execution
return analyzedResults.map(analysis => ({
  json: {
    ...analysis,
    alert_message: `üö® COPYRIGHT INFRINGEMENT DETECTED\n\nüìç URL: ${analysis.url}\nüìÑ Title: ${analysis.title}\n‚ö†Ô∏è Risk Level: ${analysis.risk_level.toUpperCase()}\nüîç Issues: ${analysis.infringement_type.join(', ')}\nüìù Content: ${analysis.content_snippet?.substring(0, 200)}...\nüìÖ Detected: ${analysis.date_found}`
  }
}));

// Get analyzed infringement data
const infringementData = $input.all()[0].json;

// Define thresholds and rules for legal action
const legalActionRules = {
  immediate_action: {
    conditions: [
      'high_similarity_text',
      'unauthorized_brand_usage',
      'competitor_usage'
    ],
    risk_level: 'high'
  },
  monitoring_required: {
    conditions: [
      'moderate_similarity_text'
    ],
    risk_level: 'medium'
  }
};

// Function to determine legal action required
function determineLegalAction(data) {
  const actionPlan = {
    immediate_cease_desist: false,
    dmca_takedown: false,
    legal_consultation: false,
    monitoring_only: false,
    evidence_collection: true, // Always collect evidence
    priority: 'low'
  };

  // Check for immediate action conditions
  const hasImmediateConditions = data.infringement_type.some(type => 
    legalActionRules.immediate_action.conditions.includes(type)
  );

  if (hasImmediateConditions && data.risk_level === 'high') {
    actionPlan.immediate_cease_desist = true;
    actionPlan.dmca_takedown = true;
    actionPlan.legal_consultation = true;
    actionPlan.priority = 'high';
  } else if (data.risk_level === 'medium') {
    actionPlan.monitoring_only = true;
    actionPlan.priority = 'medium';
  }

  return actionPlan;
}

// Generate legal action plan
const legalAction = determineLegalAction(infringementData);

// Create comprehensive report
const legalReport = {
  case_id: `COPY-${Date.now()}`,
  detected_date: infringementData.date_found,
  infringement_details: {
    url: infringementData.url,
    domain: infringementData.domain,
    title: infringementData.title,
    content_snippet: infringementData.content_snippet,
    infringement_types: infringementData.infringement_type,
    similarity_scores: infringementData.similarity_scores,
    risk_assessment: infringementData.risk_level
  },
  recommended_actions: legalAction,
  next_steps: [],
  estimated_timeline: ''
};

// Define next steps based on action plan
if (legalAction.immediate_cease_desist) {
  legalReport.next_steps.push(
    '1. Send cease and desist letter within 24 hours',
    '2. File DMCA takedown notice with hosting provider',
    '3. Schedule legal consultation within 48 hours',
    '4. Document all evidence and screenshots',
    '5. Monitor for compliance'
  );
  legalReport.estimated_timeline = '24-72 hours for initial action';
} else if (legalAction.monitoring_only) {
  legalReport.next_steps.push(
    '1. Add to monitoring watchlist',
    '2. Collect additional evidence over 7 days',
    '3. Assess if infringement escalates',
    '4. Prepare preliminary legal documentation'
  );
  legalReport.estimated_timeline = '7-14 days monitoring period';
}

console.log(`Legal action determination complete for case ${legalReport.case_id}`);

return {
  json: {
    ...legalReport,
    alert_priority: legalAction.priority,
    requires_immediate_attention: legalAction.immediate_cease_desist
  }
};

