parameters.function:
let summary = "";

if (items.length > 0) {
  summary = `**Found ${items.length} new/updated items!**\n\n`;
  for (const item of items) {
    // Assuming you extracted 'JobTitle' and 'JobLink' from HTML Extract
    const title = item.json.JobTitle || item.json.ProductName || 'N/A';
    const link = item.json.JobLink || 'No link';
    const otherInfo = item.json.StockStatus ? ` (Status: ${item.json.StockStatus})` : '';
    summary += `* **${title}**${otherInfo}\n  Link: ${link}\n\n`;
  }
} else {
  summary = "No new items found during this check.";
}

return [{ json: { notificationMessage: summary } }];
