const { JSDOM } = require('jsdom');

const deals = [];

for (const item of items) {
  const dom = new JSDOM(item.json.dealHtml);
  const doc = dom.window.document;

  const titleElement = doc.querySelector('div.name > a');
  const linkElement = doc.querySelector('div.name > a');
  const currentPriceElement = doc.querySelector('div.price-wrapper > div.price.current');
  const originalPriceElement = doc.querySelector('div.price-wrapper > div.price.original');
  const discountElement = doc.querySelector('div.price-wrapper > div.price.discount');
  const platformElements = doc.querySelectorAll('div.platforms > span.platform');

  const gameTitle = titleElement ? titleElement.textContent.trim() : 'N/A';
  const gameLink = linkElement ? `https://www.dekudeals.com${linkElement.getAttribute('href')}` : 'N/A';
  const currentPrice = currentPriceElement ? currentPriceElement.textContent.trim() : 'N/A';
  const originalPrice = originalPriceElement ? originalPriceElement.textContent.trim() : 'N/A';
  const discount = discountElement ? discountElement.textContent.trim() : 'N/A';
  const platforms = Array.from(platformElements).map(p => p.textContent.trim()).join(', ');

  // Create a unique ID for the deal for tracking in SQLite
  // Combination of title, platform, and current price should be fairly unique
  const dealUniqueId = `${gameTitle}|${platforms}|${currentPrice}`;

  deals.push({
    json: {
      dealUniqueId: dealUniqueId,
      gameTitle: gameTitle,
      gameLink: gameLink,
      platforms: platforms,
      currentPrice: currentPrice,
      originalPrice: originalPrice,
      discount: discount
    }
  });
}

return deals;

