const crypto = require('crypto');

const newItems = [];

for (const item of items) {
  const link = item.json.link || item.json.title; // Use link or title as primary unique identifier
  const linkHash = crypto.createHash('md5').update(link).digest('hex');

  // Add the hash to the item for later use
  item.json.linkHash = linkHash;

  // Check if this item (based on linkHash) is already in the database
  const result = await n8n.getNodeParameter('SQLite: Check If Processed', 'query', { json: item.json }, { execute: true });

  if (result.length === 0) {
    // Item not found in DB, it's new
    newItems.push(item);
  }
}

return newItems;

messages.content:
You are a sentiment analyzer and summarizer for company mentions. For the following text related to '{{ $node["Set Company Details"].json.companyName }}', determine its overall sentiment (Positive, Negative, or Neutral) and provide a concise 2-sentence summary. Output in JSON format:

{
  "sentiment": "[Positive/Negative/Neutral]",
  "summary": "[Concise summary]"
}

parameters.function:
const results = {
  positive: [],
  neutral: [],
  negative: [],
  noAnalysis: []
};

for (const item of items) {
  try {
    const aiOutput = JSON.parse(item.json.choices[0].message.content);
    const sentiment = aiOutput.sentiment ? aiOutput.sentiment.toLowerCase() : 'unknown';
    const summary = aiOutput.summary || 'No summary provided.';

    const data = {
      source: item.json.source,
      title: item.json.title,
      link: item.json.link,
      summary: summary,
      publishedAt: item.json.publishedAt,
      linkHash: item.json.linkHash // Pass the hash for database insertion
    };

    if (sentiment === 'positive') {
      results.positive.push(data);
    } else if (sentiment === 'negative') {
      results.negative.push(data);
    } else if (sentiment === 'neutral') {
      results.neutral.push(data);
    } else {
      results.noAnalysis.push(data);
    }
  } catch (e) {
    results.noAnalysis.push({ ...item.json, summary: `AI analysis failed: ${e.message}` });
  }
}

return [{ json: results }];

const data = items[0].json;
const companyName = $node["Set Company Details"].json.companyName;

let emailBody = `Good morning! Here's your daily online presence report for **${companyName}**.\n\n`;

// Helper to format a list of mentions
const formatMentions = (mentions, heading) => {
  if (mentions.length === 0) return '';

  let formatted = `### ${heading} (${mentions.length} mentions)\n`;
  mentions.forEach(m => {
    formatted += `* **[${m.source}]** ${m.title}\n` +
                 `    Summary: ${m.summary}\n` +
                 `    Link: ${m.link}\n` +
                 `    (Published: ${new Date(m.publishedAt).toLocaleDateString()})\n\n`;
  });
  return formatted;
};

emailBody += formatMentions(data.positive, 'Positive Mentions');
emailBody += formatMentions(data.neutral, 'Neutral Mentions');
emailBody += formatMentions(data.negative, 'Negative Mentions');
emailBody += formatMentions(data.noAnalysis, 'Mentions with No Analysis / Error');


// Check if any new mentions were processed
if (data.positive.length + data.neutral.length + data.negative.length + data.noAnalysis.length === 0) {
  emailBody = `Good morning! No *new* online mentions found for **${companyName}** today.`;
}

return [{ json: { emailSubject: `ðŸ“Š Daily Online Presence Report for ${companyName}`, emailBody: emailBody } }];
