// Extract update data from all messages
const allMessages = $input.all();

// Current time
const now = new Date();
const day = String(now.getDate()).padStart(2, '0');
const month = String(now.getMonth() + 1).padStart(2, '0');
const year = now.getFullYear();
const hours = String(now.getHours()).padStart(2, '0');
const minutes = String(now.getMinutes()).padStart(2, '0');
const sentDateTime = `${day}.${month}.${year}, ${hours}:${minutes}`;

// Create updates for each row
const updates = [];
const processedRows = new Set();

// Process ALL messages - both greeting and reminders
allMessages.forEach(msg => {
  // For greeting messages
  if (msg.json.isGreeting === true && msg.json.rowIndexes) {
    const rowIndexes = msg.json.rowIndexes || [];
    rowIndexes.forEach(rowIndex => {
      if (rowIndex && !isNaN(rowIndex) && !processedRows.has(rowIndex)) {
        processedRows.add(rowIndex);
        updates.push({
          json: {
            values: [["Yes", sentDateTime, "1"]], // Initial counter = 1
            _rowIndex: Number(rowIndex)
          }
        });
      }
    });
  }

  // For reminder messages
  if (msg.json.isReminder === true && msg.json.rowIndex) {
    const rowIndex = msg.json.rowIndex;
    const count = msg.json.count || 1;
    if (rowIndex && !isNaN(rowIndex) && !processedRows.has(rowIndex)) {
      processedRows.add(rowIndex);
      updates.push({
        json: {
          values: [["Yes", sentDateTime, String(count)]],
          _rowIndex: Number(rowIndex)
        }
      });
    }
  }
});

console.log(`Prepared ${updates.length} updates`);
return updates.length > 0 ? updates : [];

// Search and form reminders with check of current and past time, including repeated ones
const items = $input.all();
const now = new Date();
const todayStr = now.toLocaleDateString('en-US');
const executorsToNotify = {};
const reminderMessages = [];

// Get current hours and minutes
const currentHours = now.getHours();
const currentMinutes = now.getMinutes();
const currentSeconds = now.getSeconds();
const currentTimeInMinutes = currentHours * 60 + currentMinutes;

// Function to parse sent date
function parseSentDate(sentDateStr) {
  if (!sentDateStr || sentDateStr.trim() === '') return null;
  try {
    const [datePart, timePart] = sentDateStr.split(', ');
    const [day, month, year] = datePart.split('.');
    const [hours, minutes] = timePart.split(':');
    return new Date(parseInt(year), parseInt(month) - 1, parseInt(day), parseInt(hours), parseInt(minutes));
  } catch (e) {
    console.error('Error parsing sent date:', sentDateStr, e);
    return null;
  }
}

// Function to parse interval
function parseInterval(intervalStr) {
  if (!intervalStr || intervalStr.trim() === '') return 0;
  try {
    const [hours, minutes] = intervalStr.split(':').map(Number);
    return (hours || 0) * 60 * 60 * 1000 + (minutes || 0) * 60 * 1000;
  } catch (e) {
    console.error('Error parsing interval:', intervalStr, e);
    return 0;
  }
}

// Extended logging
console.log('='.repeat(50));
console.log(`TIME CHECK (PHOTO REPORTS)`);
console.log(`Current date and time: ${now.toLocaleString('en-US')}`);
console.log(`Current time: ${currentHours}:${currentMinutes.toString().padStart(2, '0')}:${currentSeconds.toString().padStart(2, '0')}`);
console.log(`Minutes from start of day: ${currentTimeInMinutes}`);
console.log(`Total records to check: ${items.length - 1}`);
console.log('='.repeat(50));

// Counters for debugging
let todayCount = 0;
let pastTimeCount = 0;
let currentTimeCount = 0;
let notSentCount = 0;
let toSendNewCount = 0;
let toSendReminderCount = 0;

items.forEach((item, index) => {
  if (index === 0) return; // Skip header

  const executorId = String(item.json['Executor ID'] || '');
  const executorName = item.json['Executor'] || '';
  const reminderDateRaw = item.json['Reminder Date'];
  const reminderTimeRaw = item.json['Reminder Time'];
  const sent = item.json['Reminder Sent'];
  const sentDateStr = item.json['Reminder Sent Date'];
  const reportId = item.json['Report ID'];
  const reportType = item.json['Report Type'] || item.json['Document Type'] || 'Unknown type';
  const reportDate = item.json['Report Date'];
  const intervalMs = parseInterval(item.json['Reminder Interval if No Report']);
  const oldCount = parseInt(item.json['Reminder Count'] || '0', 10);

  if (!executorId || !reminderDateRaw || !reportId || !executorName || reminderTimeRaw === undefined) {
    return;
  }

  // Convert date
  let reminderDate;
  try {
    if (typeof reminderDateRaw === 'number') {
      reminderDate = new Date((reminderDateRaw - 25569) * 86400 * 1000);
    } else if (typeof reminderDateRaw === 'string' && reminderDateRaw.includes('.')) {
      const [day, month, year] = reminderDateRaw.split('.');
      reminderDate = new Date(parseInt(year), parseInt(month) - 1, parseInt(day));
    } else {
      reminderDate = new Date(reminderDateRaw);
    }
    reminderDate.setHours(0, 0, 0, 0);
  } catch (e) {
    console.error(`  ‚ùå Error converting date for row ${index + 2}:`, e);
    return;
  }

  // Convert time from Excel format
  let reminderHours, reminderMinutes;
  try {
    if (typeof reminderTimeRaw === 'number') {
      const totalMinutes = Math.round(reminderTimeRaw * 24 * 60);
      reminderHours = Math.floor(totalMinutes / 60);
      reminderMinutes = totalMinutes % 60;
    } else if (typeof reminderTimeRaw === 'string') {
      const cleanTime = reminderTimeRaw.trim();

      // Handle multiple time values (take first)
      if (cleanTime.includes(' ')) {
        const firstTime = cleanTime.split(' ')[0];
        if (/^\d{1,2}:\d{2}$/.test(firstTime)) {
          const [h, m] = firstTime.split(':');
          reminderHours = parseInt(h);
          reminderMinutes = parseInt(m);
        } else {
          reminderHours = parseInt(firstTime);
          reminderMinutes = 0;
        }
      } else if (/^\d{1,2}:\d{2}$/.test(cleanTime)) {
        const [h, m] = cleanTime.split(':');
        reminderHours = parseInt(h);
        reminderMinutes = parseInt(m);
      } else if (/^\d{1,2}:\d{2}:\d{2}$/.test(cleanTime)) {
        const [h, m] = cleanTime.split(':');
        reminderHours = parseInt(h);
        reminderMinutes = parseInt(m);
      } else if (cleanTime.includes('AM') || cleanTime.includes('PM')) {
        const timeParts = cleanTime.replace(/[AP]M/i, '').trim().split(':');
        reminderHours = parseInt(timeParts[0]);
        reminderMinutes = parseInt(timeParts[1] || '0');
        if (cleanTime.toUpperCase().includes('PM') && reminderHours < 12) {
          reminderHours += 12;
        } else if (cleanTime.toUpperCase().includes('AM') && reminderHours === 12) {
          reminderHours = 0;
        }
      } else {
        console.error(`  ‚ùå Unknown time format for row ${index + 2}:`, reminderTimeRaw);
        return;
      }
    } else {
      console.error(`  ‚ùå Unknown time type for row ${index + 2}:`, typeof reminderTimeRaw, reminderTimeRaw);
      return;
    }
  } catch (e) {
    console.error(`  ‚ùå Error converting time for row ${index + 2}:`, e);
    return;
  }

  const reminderTimeInMinutes = reminderHours * 60 + reminderMinutes;

  // Check if date matches today
  const today = new Date();
  today.setHours(0, 0, 0, 0);
  const isToday = reminderDate.getTime() === today.getTime();

  const isTimeNowOrPast = reminderTimeInMinutes <= currentTimeInMinutes;
  const notSent = !sent || sent.toString().trim() === '' || sent !== 'Yes';
  const noSentDate = !sentDateStr || sentDateStr.toString().trim() === '';
  const isNotSent = notSent && noSentDate;
  const noReport = !reportDate || reportDate.toString().trim() === '';

  // Update counters
  if (isToday) {
    todayCount++;
    if (isTimeNowOrPast) {
      if (reminderTimeInMinutes === currentTimeInMinutes) {
        currentTimeCount++;
      } else {
        pastTimeCount++;
      }
    }
    if (isNotSent) notSentCount++;
  }

  // Logic for new reminders
  if (isToday && isTimeNowOrPast && isNotSent && noReport) {
    toSendNewCount++;

    if (!executorsToNotify[executorId]) {
      executorsToNotify[executorId] = {
        chatId: executorId,
        executorName: executorName,
        reports: [],
        rowIndexes: []
      };
    }

    executorsToNotify[executorId].reports.push({ 
      reportId: reportId, 
      reportType: reportType,
      rowIndex: index + 2,
      reminderTime: `${reminderHours}:${reminderMinutes.toString().padStart(2, '0')}`,
      isPast: reminderTimeInMinutes < currentTimeInMinutes
    });
    executorsToNotify[executorId].rowIndexes.push(index + 2);
  }

  // Logic for repeated reminders
  if (!isNotSent && noReport && intervalMs > 0) {
    const sentDate = parseSentDate(sentDateStr);
    if (sentDate && (now.getTime() - sentDate.getTime()) >= intervalMs) {
      toSendReminderCount++;
      reminderMessages.push({
        json: {
          chat_id: executorId,
          text: `üîî <b>Repeated reminder</b>\n\nüìã Report <b>${reportId} ${reportType}</b> is still not sent.\n\nPlease send the photo report (and if needed, a comment) in response to the first message in the chat for this task.`,
          parse_mode: "HTML",
          isReminder: true,
          reportId: reportId,
          reportType: reportType,
          rowIndex: index + 2,
          executorName: executorName,
          count: oldCount + 1,
          _isRealMessage: true
        }
      });
    }
  }
});

// Summary statistics
console.log('\n' + '='.repeat(50));
console.log('SUMMARY STATISTICS:');
console.log(`- Records with today's date: ${todayCount}`);
console.log(`- Records with current time: ${currentTimeCount}`);
console.log(`- Records with past time: ${pastTimeCount}`);
console.log(`- Records not sent: ${notSentCount}`);
console.log(`- New to send: ${toSendNewCount}`);
console.log(`- Repeated to send: ${toSendReminderCount}`);
console.log(`- Unique executors: ${Object.keys(executorsToNotify).length}`);
console.log('='.repeat(50));

// Form messages for each executor
const messages = [];

Object.values(executorsToNotify).forEach(data => {
  const reportsCount = data.reports.length;

  let greetingText = '';
  if (reportsCount === 1) {
    greetingText = `You have 1 reminder.`;
  } else {
    greetingText = `You have ${reportsCount} reminders.`;
  }

  const greetingMessage = `Hello, ${data.executorName}!\n\nüìÖ <b>Date:</b> ${todayStr}\n\n\nüîî ${greetingText}\n\n\nYou will receive the list of reports in the following messages.\nTo send a report, select it in the messages and reply to it by sending a photo (if needed, with a comment)\n\n‚ö†Ô∏è <b>Please note:</b>\n‚Ä¢ The photo must be readable\n‚Ä¢ Send it exactly in response to the message with the report\n‚Ä¢ You can send multiple photos\n\n‚úÖ Confirm receipt: <b>/read</b>\n\n‚ùì If you have any questions, write /help\n`;

  messages.push({
    json: {
      chat_id: data.chatId,
      text: greetingMessage,
      parse_mode: "HTML",
      isGreeting: true,
      executorName: data.executorName,
      rowIndexes: data.rowIndexes,
      reportIds: data.reports.map(r => r.reportId).join(', '),
      _isRealMessage: true
    }
  });

  data.reports.forEach((report) => {
    const reportMessage = `üìã Reminder to send:\n<b>üî∏ ${report.reportId} ${report.reportType}</b>\n\n<b>‚û°Ô∏è How to send the report:</b>\n1. Take a clear photo of the document\n2. IMPORTANT: Reply to this message with the photo\n3. Add a comment if necessary`;

    messages.push({
      json: {
        chat_id: data.chatId,
        text: reportMessage,
        parse_mode: "HTML",
        isReportMessage: true,
        reportId: report.reportId,
        reportType: report.reportType,
        rowIndex: report.rowIndex,
        executorName: data.executorName,
        _isRealMessage: true
      }
    });
  });
});

// Add repeated reminders
messages.push(...reminderMessages);

console.log(`\nüí¨ Formed ${messages.length} messages to send`);

if (messages.length === 0) {
  console.log('üì≠ No messages to send at the moment');
  return [];
}

return messages;

// Search and form reminders with check of current and past time, including repeated reminders
