    let priorityText = '';
    switch (task.priority) {
      case 'high':
        priorityEmoji = 'üî¥';
        priorityText = 'High priority';
        break;
      case 'medium':
        priorityEmoji = 'üü°';
        priorityText = 'Medium priority';
        break;
      case 'low':
        priorityEmoji = 'üü¢';
        priorityText = 'Low priority';
