// Extract data from geolocation for photo reports
const input = $node['Process incoming message1'].json;

// Check input data
if (!input || !input.originalMessage) {
  return [{
    json: {
      error: true,
      errorMessage: 'Missing message data',
      chatId: input?.chatId || 'unknown'
    }
  }];
}

const message = input.originalMessage;
const chatId = String(message.chat?.id || '');
const replyTo = input.replyToMessage;
const userName = (message.from?.first_name || '') + ' ' + (message.from?.last_name || '');

// Check for geolocation presence
if (!message.location) {
  return [{
    json: {
      error: true,
      errorMessage: 'No geolocation in message',
      chatId: chatId,
      originalMessage: message
    }
  }];
}

const location = message.location;
const latitude = location.latitude;
const longitude = location.longitude;

// Parse report information - IMPORTANT: Task ID
let taskId = null; // Task ID

if (replyTo?.text) {
  const replyText = replyTo.text;

  // Parse ID from format: üî∏ ID4 Photo of primary...
  const idMatch = replyText.match(/üî∏\s*(ID\d+)\s+(.+?)(?=\n|$)/i);
  if (idMatch) {
    taskId = idMatch[1]; // This is the Task ID
  }

  // Alternative format for manual reports
  if (!taskId) {
    const manualMatch = replyText.match(/(MANUAL_\d+)/i);
    if (manualMatch) {
      taskId = manualMatch[1]; // Task ID for manual report
    }
  }
}

// Validate Task ID
if (!taskId) {
  console.error('Could not parse task ID from:', replyTo?.text);
  return [{
    json: {
      error: true,
      errorMessage: 'Could not determine task ID. Ensure you are replying to the correct reminder message.',
