// Functions for processing text reports
function extractProjectInfo(messageText) {
  const objectMatch = messageText?.match(/\|\s*([^,]+),\s*([^\n]+)/);
  return {
    object: objectMatch ? objectMatch[1].trim() : foremanContext.object || "Not specified",
    section: objectMatch ? objectMatch[2].trim() : foremanContext.section || "Not specified"
  };
}

function extractTaskIdFromMessage(messageText) {
  let match = messageText.match(/\*\*(ID\d+)\*\*/i);
  if (match) return match[1];

  match = messageText.match(/priority:\s*\n\s*(ID\d+)/i);
  if (match) return match[1];

  match = messageText.match(/priority:\s*(ID\d+)/i);
  if (match) return match[1];

  match = messageText.match(/^(ID\d+)/mi);
  if (match) return match[1];

  return null;
}

function extractTaskNameFromMessage(messageText) {
  let match = messageText.match(/ID\d+\s+([^|]+)/i);
  if (match) return match[1].trim();

  match = messageText.match(/\*\*ID\d+\*\*\s+([^|]+)/i);
  return match ? match[1].trim() : null;
}

// Processing text reports for tasks
const replyToMessage = message.reply_to_message?.text || '';
const projectInfo = extractProjectInfo(replyToMessage);
const isTaskReply = replyToMessage.includes('priority:') && replyToMessage.includes('How to send report:');

if (message.text && !message.text.startsWith('/') && isTaskReply) {
  const lines = text.split('\n').filter(line => line.trim());
  const taskId = extractTaskIdFromMessage(replyToMessage);
  const taskName = extractTaskNameFromMessage(replyToMessage);

  if (taskId && taskName) {
    const status = lines[0] ? lines[0].trim().toLowerCase() : '';
    const comment = lines[1] ? lines.slice(1).join(' ').trim() : '';

    return [{
      json: {
        action: "check_task_ownership",
        chat_id: chatId,
        taskId: taskId,
        foremanId: String(chatId),
        status: status,
        comment: comment,
        replyToMessage: replyToMessage,
        projectInfo: projectInfo,
        foremanName: foremanContext.foremanName || userName,
        today: today,
        time: time
      }
    }];
  } else {
    return [{
      json: {
        action: "send_command_response",
        chat_id: chatId,
        message: `‚ùå **Error!**

        Could not determine task ID from the message. Please reply to the task message again.`
      }
    }];
  }
}

// Parsing structured report
let reports = [];
if (message.text && !message.text.startsWith('/')) {
  const lines = text.split('\n').filter(line => line.trim());

  if (text.toLowerCase().includes('task:') && text.toLowerCase().includes('status:')) {
    let currentReport = null;
    let currentField = null;
    let parsedObject = null;
    let parsedSection = null;

    for (let i = 0; i < lines.length; i++) {
      const line = lines[i].trim();
      const lowerLine = line.toLowerCase();

      if (lowerLine.includes('object:')) {
        parsedObject = line.substring(line.toLowerCase().indexOf('object:') + 7).trim();
        continue;
      }
      if (lowerLine.includes('section:')) {
        parsedSection = line.substring(line.toLowerCase().indexOf('section:') + 8).trim();
        continue;
      }

      if (lowerLine.includes('task:')) {
        if (currentReport && currentReport.Task) {
          reports.push(currentReport);
        }
        currentReport = {
          "Date": today,
          "Time": time,
          "Executor": foremanContext.foremanName || userName,
          "Chat ID": chatId,
          "Executor ID": chatId,
          "Object": parsedObject || projectInfo.object,
          "Section": parsedSection || projectInfo.section,
          "Task": line.substring(line.toLowerCase().indexOf('task:') + 5).trim(),
          "Status": "Not specified",
          "Percentage": 0,
          "Comment": "",
          "Problems": "",
          "Report Type": "Detailed",
          "Report Date": `${today} ${time}`
        };
        currentField = null;
      }
      else if (currentReport) {
        if (lowerLine.includes('status:')) {
          const statusValue = line.substring(line.toLowerCase().indexOf('status:') + 7).trim();
          const ddcmarker = "Workflow by Artem Boiko - DataDrivenConstruction.io";
          currentReport["Status"] = statusValue;
          if (statusValue.toLowerCase().match(/completed/) && !statusValue.toLowerCase().includes('not')) {
            currentReport["Percentage"] = 100;
          } else if (statusValue.toLowerCase().includes('partially')) {
            currentReport["Percentage"] = 50;
          } else if (statusValue.toLowerCase().match(/not\s*completed/)) {
            currentReport["Percentage"] = 0;
          }
          currentField = null;
        }
        else if (lowerLine.includes('percentage:')) {
          const percentValue = line.substring(line.toLowerCase().indexOf('percentage:') + 11).trim();
          currentReport["Percentage"] = parseInt(percentValue.replace('%', '')) || 0;
          currentField = null;
        }
        else if (lowerLine.includes('comment:')) {
          currentReport["Comment"] = line.substring(line.toLowerCase().indexOf('comment:') + 8).trim();
          currentField = "Comment";
        }
        else if (lowerLine.includes('problems:') || lowerLine.includes('problem:')) {
          const problemIndex = lowerLine.includes('problems:') ? 
            line.toLowerCase().indexOf('problems:') + 9 : 
            line.toLowerCase().indexOf('problem:') + 8;
          currentReport["Problems"] = line.substring(problemIndex).trim();
          currentField = "Problems";
        }
        else if (currentField && line.trim() && !lowerLine.includes(':')) {
          currentReport[currentField] += " " + line.trim();
        }
      }
    }

    if (currentReport && currentReport.Task) {
      reports.push(currentReport);
    }
  }
}

if (reports.length > 0) {
  return [{
    json: {
      action: "process_report",
      chat_id: chatId,
      reports: reports
    }
  }];
}

// If nothing recognized, send help
if (!messageText.startsWith('/') && reports.length === 0) {
  return [{
    json: {
      action: "send_message",
      chatId: chatId,
      message: `‚ùì I don't understand your message.\n\n**Available commands:**\n/read ‚Äî confirm reading reminders\n/received ‚Äî confirm receiving tasks\n/help ‚Äî get help\n\nIf you need help ‚Äî write /help`
    }
  }];
}

// Return reports for saving
return reports.map(report => ({ json: report }));
