// Parse SERP API response and find our website rankings
const items = [];

for (const item of $input.all()) {
  const serpData = item.json;
  const keyword = item.json.keyword || 'Unknown';
  const targetUrl = item.json.url || '';
  const previousRank = parseInt(item.json.previous_rank) || 0;

  let currentRank = null;
  let foundUrl = '';

  // Search through organic results
  if (serpData.organic_results && Array.isArray(serpData.organic_results)) {
    for (let i = 0; i < serpData.organic_results.length; i++) {
      const result = serpData.organic_results[i];
      const resultUrl = result.link || '';

      // Check if this result matches our target URL (domain matching)
      if (targetUrl && resultUrl.includes(targetUrl.replace('https://', '').replace('http://', '').split('/')[0])) {
        currentRank = result.position || (i + 1);
        foundUrl = resultUrl;
        break;
      }
    }
  }

  // Calculate ranking change
  const rankingChange = previousRank && currentRank ? (previousRank - currentRank) : 0;
  const rankingDrop = currentRank && previousRank ? (currentRank - previousRank) : 0;

  // Determine status
  let status = 'not_found';
  if (currentRank) {
    if (rankingChange > 0) status = 'improved';
    else if (rankingChange < 0) status = 'dropped';
    else status = 'stable';
  }

  items.push({
    json: {
      keyword: keyword,
      target_url: targetUrl,
      current_rank: currentRank,
      previous_rank: previousRank,
      ranking_change: rankingChange,
      ranking_drop: rankingDrop,
      status: status,
      found_url: foundUrl,
      check_date: new Date().toISOString().split('T')[0],
      needs_alert: rankingDrop > 2 || currentRank === null
    }
  });
}

return items;

