parameters.jsCode:
// Generate comprehensive SEO monitoring summary
const allItems = $input.all();
const processedKeywords = allItems.length;
const alertsSent = allItems.filter(item => item.json.needs_alert).length;
const improvements = allItems.filter(item => item.json.status === 'improved').length;
const drops = allItems.filter(item => item.json.status === 'dropped').length;
const stable = allItems.filter(item => item.json.status === 'stable').length;
const notFound = allItems.filter(item => item.json.status === 'not_found').length;

// Calculate average ranking
const rankedKeywords = allItems.filter(item => item.json.current_rank);
const avgRanking = rankedKeywords.length > 0 ? 
  (rankedKeywords.reduce((sum, item) => sum + item.json.current_rank, 0) / rankedKeywords.length).toFixed(1) : 'N/A';

// Log detailed summary
console.log('=== SEO MONITORING SUMMARY ===');
console.log(`Execution Date: ${new Date().toISOString()}`);
console.log(`Total Keywords Processed: ${processedKeywords}`);
console.log(`Alerts Triggered: ${alertsSent}`);
console.log(`Ranking Status:`);
console.log(`  - Improved: ${improvements}`);
console.log(`  - Dropped: ${drops}`);
console.log(`  - Stable: ${stable}`);
console.log(`  - Not Found: ${notFound}`);
console.log(`Average Ranking: ${avgRanking}`);
console.log('================================');

return [{
  json: {
    execution_summary: {
      date: new Date().toISOString(),
      keywords_processed: processedKeywords,
      alerts_sent: alertsSent,
      improvements: improvements,
      drops: drops,
      stable: stable,
      not_found: notFound,
      average_ranking: avgRanking,
      success: true
    }
  }
}];
