// Lấy Start_LastMonth và End_LastMonth từ node Format Date
const startCurrentRaw = $('Format Date').first().json.Start_LastMonth;
const endCurrentRaw = $('Format Date').first().json.End_LastMonth;

// Debug: In ra giá trị thô của Start_LastMonth và End_LastMonth
console.log('Start Current Raw:', startCurrentRaw);
console.log('End Current Raw:', endCurrentRaw);

// Hàm chuyển đổi định dạng ngày DD/MM/YYYY hoặc YYYY-MM-DD thành Date object
function parseDate(dateStr) {
  if (!dateStr || typeof dateStr !== 'string') {
    console.log('Invalid date string:', dateStr);
    return null;
  }
  if (dateStr.includes('/')) {
    const [day, month, year] = dateStr.split('/').map(Number);
    return new Date(year, month - 1, day);
  } else if (dateStr.includes('-')) {
    return new Date(dateStr);
  }
  console.log('Unrecognized date format:', dateStr);
  return null;
}

// Chuyển đổi Start_LastMonth và End_LastMonth thành Date object
const startCurrent = parseDate(startCurrentRaw);
const endCurrent = parseDate(endCurrentRaw);

// Debug: In ra giá trị sau khi chuyển đổi
console.log('Start Current Parsed:', startCurrent);
console.log('End Current Parsed:', endCurrent);

// Kiểm tra nếu Start_LastMonth hoặc End_LastMonth không hợp lệ
if (!startCurrent || !endCurrent) {
  throw new Error('Invalid Start_LastMonth or End_LastMonth date format');
}

// Tạo một object để lưu trữ tổng Số tiền theo Phân loại
const aggregatedData = {};

// Hàm chuẩn hóa chuỗi: loại bỏ khoảng trắng thừa và chuyển về chữ thường
function normalizeString(str) {
  return str ? str.trim().toLowerCase() : '';
}

// Lọc và tính tổng
for (const item of $input.all()) {
  // Chuyển Date từ định dạng DD/MM/YYYY hoặc YYYY-MM-DD thành Date object
  const itemDate = parseDate(item.json.Date);

  if (!itemDate) {
    console.log('Skipping item due to invalid date:', item.json);
    continue;
  }

  const amount = Number(item.json['Số tiền']) || 0;

  // Kiểm tra nếu Date nằm trong khoảng Start_LastMonth và End_LastMonth
  if (itemDate >= startCurrent && itemDate <= endCurrent) {
    // Chuẩn hóa Phân loại trước khi tạo key
    const phanLoai = normalizeString(item.json['Phân loại']);

    // Tạo key duy nhất từ Phân loại
    const key = phanLoai;

    // Debug: In ra key để kiểm tra
    console.log('Key:', key);

    // Nếu key chưa tồn tại, khởi tạo giá trị ban đầu
    if (!aggregatedData[key]) {
      aggregatedData[key] = {
        'Phân loại': item.json['Phân loại'], // Giữ nguyên giá trị gốc để hiển thị
        'Tổng số tiền': 0
      };
    }

    // Cộng dồn Số tiền vào Tổng số tiền
    aggregatedData[key]['Tổng số tiền'] += amount;
  }
}

// Debug: In ra số lượng nhóm và dữ liệu đã gộp
console.log('Number of groups:', Object.keys(aggregatedData).length);
console.log('Aggregated Data:', aggregatedData);

// Chuyển object aggregatedData thành mảng để trả về
const result = Object.values(aggregatedData);

return result.map(item => ({ json: item }));

